public void clicked()
{
    boolean  canAdjustICMS   = false;
    boolean  canAdjustICMSST = false;
    boolean  enableICMS      = false;
    boolean  enableICMSDiff  = false;
    boolean  enableICMSST    = false;

    FiscalDocumentTaxTrans_BR   taxTrans;

    [canAdjustICMS, canAdjustICMSST] = this.canFiscalDocumentBeAdjusted(FiscalDocument_BR);

    if (canAdjustICMS || canAdjustICMSST)
    {
        for (taxTrans = FiscalDocumentTaxTrans_DS.getFirst();
        taxTrans;
        taxTrans = FiscalDocumentTaxTrans_DS.getNext())
        {
            switch (taxTrans.Type)
            {
                case TaxType_BR::ICMS:
                    enableICMS = true;
                    break;

                case TaxType_BR::ICMSDiff:
                    enableICMSDiff = true;
                    break;

                case TaxType_BR::ICMSST:
                    enableICMSST = true;
                    break;
            }

            if (enableICMS && enableICMSDiff && enableICMSST)
            {
                break;
            }
        }
    }

    FBFiscalDocumentAdjustmentICMS.enabled(canAdjustICMS && enableICMS);
    FBFiscalDocumentAdjustmentICMSDiff.enabled(canAdjustICMS && enableICMSDiff);
    FBFiscalDocumentAdjustmentICMSSt.enabled(canAdjustICMSST && enableICMSST);
}

/// <summary>
///     Verifies whether the fiscal document can be adjusted
/// </summary>
/// <param name="_fiscalDocument">
///     A <c>FiscalDocument_BR</c> table record
/// </param>
/// <returns>
///     A container with two boolean values, in the first position if ICMS can be adjusted and in the second if ICMS-ST can be adjusted
/// </returns>
private container canFiscalDocumentBeAdjusted(FiscalDocument_BR _fiscalDocument)
{
    #FiscalDocumentModels_BR

    boolean                             canAdjustICMS = false;
    boolean                             canAdjustICMSST = false;
    LogisticsPostalAddress              thirdPartyPostalAddress;
    FBBookingPeriodFiscalDocument_BR    fbBookingPeriodFiscalDocument;

    fbBookingPeriodFiscalDocument = FBBookingPeriodFiscalDocument_BR::findByFiscalDocument(_fiscalDocument.RecId);

    if (fbBookingPeriodFiscalDocument.FiscalSituation == FBFiscalDocumentSituation_BR::Regular)
    {
        if (FiscalDocModel_BR::find(_fiscalDocument.Model).CanBeAdjusted)
        {
            thirdPartyPostalAddress = LogisticsPostalAddress::findRecId(_fiscalDocument.ThirdPartyPostalAddress);

            //If the respective tax assessment exists
            canAdjustICMS   = FBTaxAssessment_BR::findByDate(_fiscalDocument.AccountingDate, TaxType_BR::ICMS, fbBookingPeriodFiscalDocument.FBBookingPeriod_BR).RecId;
            canAdjustICMSST = FBTaxAssessment_BR::findByDate(_fiscalDocument.AccountingDate, TaxType_BR::ICMSST, fbBookingPeriodFiscalDocument.FBBookingPeriod_BR, thirdPartyPostalAddress.State).RecId;
        }
    }

    return [canAdjustICMS, canAdjustICMSST];
}

public void jumpRef()
{
    Args            args;
    MenuFunction    menuFunction;

    if (EFDocumentReceivedXML_BR.RecId)
    {
        menuFunction = new MenuFunction(menuitemDisplayStr(EFDocumentReceivedXML_BR), MenuItemType::Display);

        args = new Args(formStr(EFDocumentReceivedXML_BR));
        args.record(EFDocumentReceivedXML_BR);
        args.caller(element);

        menuFunction.run(args);
    }
    else if (EFDocument_BR.RecId)
    {
        menuFunction = new MenuFunction(menuitemDisplayStr(EFDocHist_BR), MenuItemType::Display);

        args = new Args(formStr(EFDocHist_BR));
        args.record(EFDocument_BR);
        args.caller(element);

        menuFunction.run(args);
    }
}
