[Form]
public class FiscalEstablishment_BR extends FormRun implements OfficeIMenuCustomizer
{
}

public void init()
{
    super();

    boolean useAdvCert = SystemParameters::find().UseAdvCertificateStore;

    if (element.args().dataset() == tableNum(FiscalEstablishmentRelations_BR))
    {
        QueryBuildDataSource queryBuildDataSource = FiscalEstablishment_BR_ds.query().dataSourceTable(tableNum(FiscalEstablishment_BR));
        FiscalEstablishmentRelations_BR feRelations = FiscalEstablishmentRelations_BR::findRecId(element.args().record().RecId);

        queryBuildDataSource.clearDynalinks();

        QueryBuildRange queryBuildRange = queryBuildDataSource.addRange(fieldNum(FiscalEstablishment_BR, RecId));
        queryBuildRange.value(SysQuery::value(feRelations.FiscalEstablishment_BR));
    }

    FiscalEstablishment_BR_ds.object(fieldNum(FiscalEstablishment_BR, EFDocKeyVaultCertificateRef)).visible(useAdvCert);
    FiscalEstablishment_BR_ds.object(fieldNum(FiscalEstablishment_BR, EFDocDigitalCertificateSubject)).visible(!useAdvCert);
}

public void updateDesign()
{
    if (fiscalEstablishment_BR.efDocAuthority &&
        fiscalEstablishment_BR.efDocVersion &&
        fiscalEstablishment_BR.efDocEnvironment)
    {
        efDocViewWSButton.enabled(true);
    }
    else
    {
        efDocViewWSButton.enabled(false);
    }

    if (fiscalEstablishment_BR.ConsumerEFDocAuthority &&
        fiscalEstablishment_BR.ConsumerEFDocVersion &&
        fiscalEstablishment_BR.ConsumerEFDocEnvironment)
    {
        ConsumerEFDocViewWSButton.enabled(true);
    }
    else
    {
        ConsumerEFDocViewWSButton.enabled(false);
    }

    // Fiscal Books attributes
    if (BrazilParameters::isFiscalBooksEnabled_BR())
    {
        retailTabPage.visible(true);
    }
    else
    {
        retailTabPage.visible(false);
    }

    GroupNFe40TechNotes.visible(fiscalEstablishment_BR.EFDocVersion == EFiscalDocVersion_BR::V40);
    fiscalEstablishment_BR_ds.object(fieldNum(FiscalEstablishment_BR, EFDocNFeTechNotes)).visible(fiscalEstablishment_BR.EFDocNFe402018005v110 == NoYes::Yes);
    TechnicalResponsibleInformation.visible(fiscalEstablishment_BR.checkTechnicalNoteIsEnabled(EFDocConstants_BR::NFeNT2018005v120));
}

private void enableFiscalEstablishmentIE()
{
    DirPartyLocation        dirPartyLocation;
    LogisticsPostalAddress  address;
    boolean                 allowCUD;

    dirPartyLocation    = DirPartyLocation::findRec(FiscalEstablishment_BR.DirPartyLocation);
    address             = LogisticsPostalAddress::findByLocation(dirPartyLocation.Location);

    if (address.CountryRegionId != 'BRA')
    {
        allowCUD = false;
    }
    else
    {
        allowCUD = true;
    }

    FiscalEstablishmentIEPerState_BR_ds.allowCreate(allowCUD);
    FiscalEstablishmentIEPerState_BR_ds.allowEdit(allowCUD);
    FiscalEstablishmentIEPerState_BR_ds.allowDelete(allowCUD);

    ActionPaneStrip.enabled(allowCUD);
}

public int active()
{
    int ret;

    ret = super();

    this.enableDataSourceFields();
    element.updateDesign();

    this.enableFiscalEstablishmentIE();

    return ret;
}

/// <summary>
/// Handles the standard write method of the datasource.
/// </summary>
public void write()
{
    FormDataSource fds = element.dataSource(formDataSourceStr(FiscalEstablishment_BR, FiscalEstablishment_BR)) as FormDataSource;
    FiscalEstablishment_BR fiscalEstablishment = fds.cursor();

    if (fiscalEstablishment.checkTechnicalNoteIsEnabled(EFDocConstants_BR::NFeNT2019001v140)
        && !fiscalEstablishment.orig().checkTechnicalNoteIsEnabled(EFDocConstants_BR::NFeNT2019001v140))
    {
        EFDocAuthority_BR::initDefaultLimitPerEstablishment(fiscalEstablishment);
    }

    super();
}

public void jumpRef()
{
    Args                   args;
    LogisticsPostalAddress address;

    address = LogisticsPostalAddress::findByLocation(DirPartyLocation::findRec(FiscalEstablishment_BR.DirPartyLocation).Location);

    args    = new Args();

    args.caller(element);
    args.lookupTable(tableNum(LogisticsPostalAddress));
    args.lookupField(fieldnum(LogisticsPostalAddress, RecId));
    args.lookupValue(int642str(address.RecId));
    args.parmEnumType(enumnum(LogisticsLocationAddressActionButtons));

    LogisticsPostalAddressFormHandler::main(args, true);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    QueryBuildDataSource    qbds;
    Query                   query = new Query();

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(LogisticsAddressState), _formControl);

    sysTableLookup.addLookupfield(fieldNum(LogisticsAddressState, StateId));
    sysTableLookup.addLookupfield(fieldNum(LogisticsAddressState, CountryRegionId));
    sysTableLookup.addLookupfield(fieldNum(LogisticsAddressState, Name));

    qbds = query.addDataSource(tableNum(LogisticsAddressState));
    qbds.addRange(fieldNum(LogisticsAddressState, CountryRegionId)).value(queryValue(FiscalEstablishmentIEPerState_BR.CountryRegionId));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void executeQuery()
{
    super();

    if (element.args().dataset() == tableNum(FiscalEstablishmentRelations_BR))
    {
        FiscalEstablishmentRelations_BR feRelations = FiscalEstablishmentRelations_BR::findRecId(element.args().record().RecId);

        FiscalEstablishmentRelations_BR_ds.positionToRecord(feRelations);
    }
}

public int active()
{
    int ret = super();

    FiscalEstablishmentRelations_BR_ds.allowDelete(!fiscalEstablishmentRelations_BR.submitted());

    return ret;
}

/// <summary>
/// Jumps to the Key Vault parameters of the current Certificate.
/// </summary>
public void jumpRef()
{
    KeyVaultParameters::jumpRef(FiscalEstablishment_BR.EFDocKeyVaultCertificateRef);
}
