[Form]
public class FiscalOrganization_BR extends FormRun implements OfficeIMenuCustomizer
{
    FormRun formRun;

}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    _menuOptions.dataEntityOptions().addEnd(OfficeMenuDataEntityOptions::construct(tableStr(FiscalOrganizationRelatedSCPEntity)));
    _menuOptions.dataEntityOptions().addEnd(OfficeMenuDataEntityOptions::construct(tableStr(FiscalOrganizationFiscalAuditorEntity)));
    _menuOptions.dataEntityOptions().addEnd(OfficeMenuDataEntityOptions::construct(tableStr(FiscalOrganizationFiscalEstablishmentEntity)));
}

/// <summary>
/// Returns the fiscal establihsment selected in the lookup
/// </summary>
/// <returns>
/// A <c>FiscalEstablishment_BR</c> table record
/// </returns>
public FiscalEstablishment_BR fiscalEstablishmentSelected()
{
    FiscalEstablishment_BR fiscalEstablishment;

    if (formRun)
    {
        fiscalEstablishment = formRun.selectRecordModeSelectedRecord();

        formRun = null;
    }

    return fiscalEstablishment;
}

/// <summary>
/// Calls the fiscal establishment form
/// </summary>
/// <param name="_fiscalEstablishment">
/// A <c>FiscalEstablishment_BR</c> table record
/// </param>
public void jumpRefFiscalEstablishment(FiscalEstablishment_BR _fiscalEstablishment)
{
    Args                    args = new Args();
    MenuFunction            menuItem;
    FiscalEstablishment_BR  fiscalEstablishment;

    menuItem = new MenuFunction(menuitemDisplayStr(FiscalEstablishment_BR), MenuItemType::Display);

    fiscalEstablishment = FiscalOrganizationFEstablishment.fiscalEstablishment();

    if (_fiscalEstablishment)
    {
        changeCompany(_fiscalEstablishment.dataAreaId)
        {
            menuItem.run(args);
        }
    }
    else
    {
        menuItem.run(args);
    }
}

public void refreshFiscalEstablishment()
{
    if (FiscalOrganization_BR.FiscalEstablishmentId)
    {
        ActionPaneStrip.enabled(true);
    }
    else
    {
        ActionPaneStrip.enabled(false);
    }

    FiscalOrganizationFEstablishment_ds.populate();
}

/// <summary>
/// Updates the form design
/// </summary>
public void updateDesign()
{
    boolean allowEditSCPCode, allowEditRelatedSCP;

    if (FiscalOrganization_BR.AssessmentRegimen == FBContribAssessmentRegimen_BR::NonCumulative
        || FiscalOrganization_BR.AssessmentRegimen == FBContribAssessmentRegimen_BR::Both)
    {
        FiscalOrganization_BR_ds.object(fieldNum(FiscalOrganization_BR, AssessmentCriteria)).allowEdit(false);
        FiscalOrganization_BR_ds.object(fieldNum(FiscalOrganization_BR, CreditAllocationMethod)).allowEdit(true);
    }
    else
    {
        FiscalOrganization_BR_ds.object(fieldNum(FiscalOrganization_BR, AssessmentCriteria)).allowEdit(true);
        FiscalOrganization_BR_ds.object(fieldNum(FiscalOrganization_BR, CreditAllocationMethod)).allowEdit(false);
    }

    allowEditSCPCode    = FiscalOrganization_BR.SCPType == FBSCPType_BR::SCP;
    allowEditRelatedSCP = FiscalOrganization_BR.SCPType == FBSCPType_BR::SCPPartner;

    FiscalOrganization_BR_ds.object(fieldNum(FiscalOrganization_BR, SCPCode)).allowEdit(allowEditSCPCode);
    FBRelatedSCP_BR_ds.allowEdit(allowEditRelatedSCP);
    RelatedSCPActionPane.enabled(allowEditRelatedSCP);
    RelatedSCPGrid.enabled(allowEditRelatedSCP);

    FBRelatedSCP_BR_ds.reread();
    FBRelatedSCP_BR_ds.refresh();

    element.refreshFiscalEstablishment();
}

private void setDataAreaId()
{
    FiscalOrganization_BR_CofinsSalesTaxExpenseLedgerDimension_BR.setDataAreaId(FiscalOrganization_BR.FiscalEstablishmentDataArea);        
    FiscalOrganization_BR_CofinsSalesTaxExpenseLedgerDimension_BR.parmCurrentLedgerCOA(Ledger::chartOfAccounts(CompanyInfo::findDataArea(FiscalOrganization_BR.FiscalEstablishmentDataArea).RecId));

    FiscalOrganization_BR_PisSalesTaxExpenseLedgerDimension_BR.setDataAreaId(FiscalOrganization_BR.FiscalEstablishmentDataArea);
    FiscalOrganization_BR_PisSalesTaxExpenseLedgerDimension_BR.parmCurrentLedgerCOA(Ledger::chartOfAccounts(CompanyInfo::findDataArea(FiscalOrganization_BR.FiscalEstablishmentDataArea).RecId));
}

public void run()
{
    super();

    element.refreshFiscalEstablishment();
}

