/// <summary>
/// Populate the datasource with the branches of the Fiscal Organization
/// </summary>
public void populate()
{
    FiscalOrganizationFEstablishment.setTmpData(FiscalOrganizationFiscalEstablishmentTmpData::populateTmpData());
}

public void delete()
{
    FiscalEstablishment_BR fiscalEstablishment = FiscalOrganizationFEstablishment.fiscalEstablishment();

    ttsBegin;

    if (fiscalEstablishment)
    {
        changeCompany(fiscalEstablishment.dataAreaId)
        {
            fiscalEstablishment.selectForUpdate(true);
            fiscalEstablishment.FiscalOrganization_BR = 0;
            fiscalEstablishment.update();
        }
    }

    super();

    ttsCommit;

}

display Name fiscalEstablishmentName(FiscalOrganizationFEstablishment_BR _fiscalOrganizationFEstablishment)
{
    return _fiscalOrganizationFEstablishment.fiscalEstablishment().Name;
}

