    return _fiscalOrganizationFEstablishment.fiscalEstablishment().CNPJCPFNum_BR;
}

public void write()
{
    FiscalEstablishment_BR fiscalEstablishment;
    super();

    fiscalEstablishment = FiscalOrganizationFEstablishment.fiscalEstablishment();

    ttsBegin;
    changeCompany(fiscalEstablishment.dataAreaId)
    {
        fiscalEstablishment.selectForUpdate(true);
        fiscalEstablishment.FiscalOrganization_BR = FiscalOrganizationFEstablishment.FiscalOrganization_BR;
        fiscalEstablishment.update();
    }
    ttsCommit;
}

