public void lookup()
{
    Args    args = new Args(formStr(FiscalEstablishmentFiscalOrgLookup_BR));

    formRun = classFactory::formRunClassOnClient(args);
    formRun.init();

    this.performFormLookup(formRun);
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = this.valueSelectedByLookup();
    }

    return ret;
}

/// <summary>
/// Compares current value with value selected by lookup.
/// </summary>
/// <returns>
/// True if values are equals, otherwise false.
/// </returns>
private boolean valueSelectedByLookup()
{
    FiscalEstablishment_BR fiscalEstablishment;

    fiscalEstablishment = element.fiscalEstablishmentSelected();

    ttsBegin;
    FiscalOrganization_BR.FiscalEstablishmentDataArea = fiscalEstablishment.dataAreaId;
    FiscalOrganization_BR.FiscalEstablishmentId = fiscalEstablishment.FiscalEstablishmentId;
    FiscalOrganization_BR.write();

    changeCompany(fiscalEstablishment.dataAreaId)
    {
        fiscalEstablishment.selectForUpdate(true);
        fiscalEstablishment.FiscalOrganization_BR = FiscalOrganization_BR.RecId;
        fiscalEstablishment.write();
    }
    ttsCommit;

    FiscalOrganization_BR_ds.refresh();
    element.refreshFiscalEstablishment();

    return FiscalOrganization_BR.FiscalEstablishmentId && FiscalOrganization_BR.FiscalEstablishmentDataArea;
}

public void lookup()
{
    Args    args;

    args = new Args(formStr(FiscalEstablishmentFiscalOrgLookup_BR));
    args.record(FiscalOrganization_BR);

    formRun = classFactory::formRunClassOnClient(args);
    formRun.init();

    this.performFormLookup(formRun);
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = this.valueSelectedByLookup();
    }

    return ret;
}

/// <summary>
/// Compares current value with value selected by lookup.
/// </summary>
/// <returns>
/// True if values are equals, otherwise false.
/// </returns>
private boolean valueSelectedByLookup()
{
    FiscalEstablishment_BR fiscalEstablishment;

    fiscalEstablishment = element.fiscalEstablishmentSelected();

    FiscalOrganizationFEstablishment.FiscalOrganization_BR = FiscalOrganization_BR.RecId;
    FiscalOrganizationFEstablishment.FiscalEstablishmentDataArea = fiscalEstablishment.dataAreaId;
    FiscalOrganizationFEstablishment.FiscalEstablishmentId = fiscalEstablishment.FiscalEstablishmentId;

    return FiscalOrganizationFEstablishment.FiscalEstablishmentId && FiscalOrganizationFEstablishment.FiscalEstablishmentDataArea;
}
