public void init()
{
    Object  callerFormObject;

    if (element.args().parmObject() && element.args().parmObject() is BudgetPlanSupplyForecastDrillThroughHelper)
    {
        calledFromBudgetPlanning = true;
    }

    if (#PdsCatchWeightEnabled)
    {
        cwFormCtrl = PdsCWFormCtrl::constructFromForm(element);
        cwFormCtrl.parmFieldIds(PdsCWValidation::fieldMapFromContainers(
            [[fieldnum(ForecastPurch, InventQty),
              fieldnum(ForecastPurch, PdsCWQty),
              false]] ));
        cwFormCtrl.parmItemFieldId(fieldnum(ForecastPurch, ItemId));
        cwFormCtrl.parmUnitFieldId(fieldnum(ForecastPurch, PdsCWUnitId));
    }

    super();

    if (!this.validateCaller())
    {
        throw(error(Error::missingFormActiveBuffer(element.design().caption())));
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    xSysLastValue::getLast(this);

    element.updateFields();

    if (element.args() && element.args().caller())
    {
        callerFormObject = element.args().caller();

        if (formHasMethod(callerFormObject, identifierstr(parmItemId)))
        {
            defaultItemId = element.args().caller().parmItemId();
        }

        if (formHasMethod(callerFormObject, identifierstr(defaultInventDim)))
        {
            defaultInventDim = element.args().caller().defaultInventDim();
        }

        if (formHasMethod(callerFormObject, identifierstr(parmForecastItemAllocationId)))
        {
            defaultItemAllocationId = element.args().caller().parmForecastItemAllocationId();
        }

        if (formHasMethod(callerFormObject, identifierstr(getFromDate)))
        {
            defaultDate = element.args().caller().getFromDate();
        }
    }
}

