public container pack()
{
    lockmodel       = lockModelId.value();
    modelId         = critModelId.text();

    lockItem        = lockItemId.value();
    itemId          = critItemId.text();

    lockItemGroup   = lockItemGroupId.value();
    itemGroupId     = critItemGroupId.text();

    lockVendAccount = lockVendAccountId.value();
    vendAccountId   = critVendAccountId.text();

    lockVendGroup   = lockVendGroupId.value();
    vendGroupId     = critVendGroupId.text();

    return [#CurrentVersion,#CurrentList];
}

public void setModifiedPriceAgreement(FieldId _fieldId)
{
    if (forecastPurch.modifiedProductDim(this.inventDim(),
                                         _fieldId,
                                         InventDimGroupSetup::newItemId(forecastPurch.ItemId)))
    {
        forecastPurch_ds.updateAmount();
    }
}

public boolean unpack(container _packedClass)
{
    int version     = RunBase::getVersion(_packedClass);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = _packedClass;

            lockModelId.value(lockmodel);
            lockItemId.value(lockItem);
            lockItemGroupId.value(lockItemGroup);
            lockVendAccountId.value(lockVendAccount);
            lockVendGroupId.value(lockVendGroup);

            critModelId.text(modelId);
            critItemId.text(itemId);
            critItemGroupId.text(itemGroupId);
            critVendAccountId.text(vendAccountId);
            critVendGroupId.text(vendGroupId);

            return true;

        default :
            return false;
    }
}

