public void updateCaption()
{
    str             captionText;

    captionText= tableId2pname(tableNum(ForecastPurch));

    if (lockModelId.value())
    {
       captionText +=', ' +  fieldPName(ForecastPurch,ModelId) + ': ' + critModelId.text();
    }

    if (lockVendGroupId.value() && lockVendGroupId.allowEdit())     // allowEdit == false => caption is set via dynalink
    {
        captionText +=', ' + fieldPName(ForecastPurch,VendGroupId) + ': ' + critVendGroupId.text();
    }

    if (lockVendAccountId.value() && lockVendAccountId.allowEdit()) // allowEdit == false => caption is set via dynalink
    {
        captionText +=', ' + fieldPName(ForecastPurch,VendAccountId) + ': ' + critVendAccountId.text();
    }

    if (lockItemGroupId.value() && lockItemGroupId.allowEdit())     // allowEdit == false => caption is set via dynalink
    {
        captionText +=', ' + fieldPName(ForecastPurch,ItemGroupId) + ': ' + critItemGroupId.text();
    }

    if (lockItemId.value() && lockItemId.allowEdit())               // allowEdit == false => caption is set via dynalink
    {
        captionText +=', ' + fieldPName(ForecastPurch,ItemId) + ': ' + critItemId.text();
    }

    element.design().caption(captionText);
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParmShow;
    NoYes           useItemAllocationKey;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_Forecast::newFromForm(element);
            }
            inventDimFormSetup.parmSkipOnHandLookUp(true);
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmShow);
            //fall through
        case InventDimFormDesignUpdate::Active,
            InventDimFormDesignUpdate::FieldChange:
            useItemAllocationKey = forecastPurch.ItemAllocateId ? NoYes::Yes : NoYes::No;
            inventDimFormSetup.parmUseItemAllocationKey(useItemAllocationKey);
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(forecastPurch.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;
        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public void updateFields()
{
        overview_modelId.visible(! lockModelId.value());
        overview_VendGroupId.visible(! (lockVendGroupId.value() || (lockVendAccountId.value() && critVendAccountId.text())));
        overview_VendAccountId.visible(! lockVendAccountId.value());
        overview_ItemGroupId.visible(! (lockItemGroupId.value() || (lockItemId.value() && critItemId.text())));
        overview_ItemId.visible(! lockItemId.value());

    element.updateCaption();
}

public boolean validateCaller()
{
    InventTable inventTable;

    switch (element.args().dataset())
    {
        case tableNum(InventTable):
            inventTable = element.args().record();
            if (!inventTable.RecId)
            {
                return checkFailed(strFmt("@SYS84897", tablePName(InventTable)));
            }
            if (!InventTable::exist(inventTable.ItemId))
            {
                return checkFailed(strFmt("@SYS5307", inventTable.ItemId));
            }
            break;
    }
    return true;
}

public void dataSourceActivePost()
{
    boolean  isCWItem;

    if (cwFormCtrl)
    {
        cwFormCtrl.dataSourceActivePost();
    }

    isCWItem = PdsGlobal::pdsIsCWItem(forecastPurch.ItemId);
    forecastPurch_ds.object(fieldNum(ForecastPurch, PurchQty)).allowEdit(!isCWItem);
    forecastPurch_ds.object(fieldNum(ForecastPurch, PurchUnitId)).allowEdit(!isCWItem);
    forecastPurch_ds.object(fieldNum(ForecastPurch, PdsCWUnitId)).skip(!isCWItem);
}

