public void linkActive()
{
    InventDimCombination    callerInventDimCombination;

    VendAccount             callerVendAccount;
    VendGroupId             callerVendGroupId;

    ItemId                  callerItemId;
    ItemGroupId             callerItemGroupId;

    ForecastInvent          callerForecastInvent;
    ForecastPurch           bufferForecastPurch;

    QueryBuildDataSource    qbs,qbsInventDim;
    QueryBuildRange         criteriaRecId;

    if (element.args().caller() && element.args().dataset())
    {
        switch (element.args().dataset())
        {
            case tableNum(VendTable):
                callerVendAccount = element.args().record().(fieldName2id(tableNum(VendTable), fieldStr(VendTable,AccountNum)));
                if (callerVendAccount)
                {
                    lockVendAccountId.value(true);
                    lockVendAccountId.allowEdit(false);

                    critVendAccountId.text(callerVendAccount);
                    critVendAccountId.allowEdit(false);
                }
                break;
            case tableNum(VendGroup):
                callerVendGroupId = element.args().record().(fieldName2id(tableNum(VendGroup), fieldStr(VendGroup,VendGroup)));
                if (callerVendGroupId)
                {
                    lockVendGroupId.value(true);
                    lockVendGroupId.allowEdit(false);

                    critVendGroupId.text(callerVendGroupId);
                    critVendGroupId.allowEdit(false);
                }
                break;
            case tableNum(InventTable):
                callerItemId = element.args().record().(fieldName2id(tableNum(InventTable), fieldStr(InventTable,ItemId)));
                if (callerItemId)
                {
                    lockItemId.value(true);
                    lockItemId.allowEdit(false);

                    critItemId.text(callerItemId);
                    critItemId.allowEdit(false);
                }
                break;
            case tableNum(InventItemGroup):
                callerItemGroupId = element.args().record().(fieldName2id(tableNum(InventItemGroup), fieldStr(InventItemGroup,ItemGroupId)));
                if (callerItemGroupId)
                {
                    lockItemGroupId.value(true);
                    lockItemGroupId.allowEdit(false);

                    critItemGroupId.text(callerItemGroupId);
                    critItemGroupId.allowEdit(false);
                }
                break;
            case tableNum(ForecastInvent):
                callerForecastInvent = element.args().record();
                select firstonly ExpandId,ModelId,ItemId from bufferForecastPurch where bufferForecastPurch.RecId == callerForecastInvent.TransRecId ;
                if (bufferForecastPurch.ModelId)
                {
                    lockModelId.value(true);
                    lockModelId.allowEdit(false);
                    critModelId.text(bufferForecastPurch.ModelId);
                    critModelId.allowEdit(false);
                }
                if (bufferForecastPurch.ItemId)
                {
                    lockItemId.value(true);
                    lockItemId.allowEdit(false);
                    critItemId.text(bufferForecastPurch.ItemId);
                    critItemId.allowEdit(false);
                }
                if ( bufferForecastPurch.ExpandId)    // if forecastInvent record is created from an expanded record then use the expandId instead of dynalink
                {
                    qbs = forecastPurch_ds.query().dataSourceTable(tableNum(ForecastPurch));
                    qbs.clearDynalinks();
                    forecastPurchExpand_ds.query().dataSourceTable(tableNum(ForecastPurch)).clearDynalinks();

                    criteriaRecId = SysQuery::findOrCreateRange(qbs, fieldNum(ForecastPurch, RecId));
                    criteriaRecId.value(queryValue(bufferForecastPurch.ExpandId));
                }
                break;
            case tableNum(InventDimCombination):
                callerInventDimCombination = element.args().record();
                if (callerInventDimCombination)
                {
                    inventDimFromInventDimCombination = callerInventDimCombination.inventDim();

                    lockItemId.value(true);
                    lockItemId.allowEdit(false);

                    critItemId.text(callerInventDimCombination.ItemId);
                    critItemId.allowEdit(false);

                    qbs = forecastPurch_ds.query().dataSourceTable(tableNum(ForecastPurch));
                    qbs.clearDynalinks();
                    qbsInventDim = inventDim_ds.query().dataSourceTable(tableNum(InventDim));

                    InventDim::queryAddRangeAndValueProductDim(qbsInventDim, inventDimFromInventDimCombination, EcoResProductDimGroupSetup::newItemId(callerInventDimCombination.ItemId), false);
                }
                break;
            case tableNum(LedgerCov):
                LedgerCov callerLedgerCov = element.args().record();

                if (callerLedgerCov)
                {
                    forecastPurchExpand_ds.query().dataSourceTable(tableNum(ForecastPurch)).clearDynalinks();
                }
                break;
        }
    }

    super();

    element.updateFields();
}

public void write()
{
    super();

    if (forecastPurch.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(forecastPurch.inventDim());
        inventDim_ds.setCurrent();
    }

    forecastPurchExpand_ds.executeQuery();
}

public boolean validateWrite()
{
    boolean ret;

    inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
    forecastPurch.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    if (!element.validateCaller())
    {
        ret = false;
    }

    return ret;
}

public void initValue()
{
    forecastPurch.ItemId = critItemId.text();

    if (defaultItemId)
    {
        forecastPurch.ItemId = defaultItemId;
    }
    else
    if (defaultItemAllocationId)
    {
        forecastPurch.ItemAllocateId = defaultItemAllocationId;
    }

    if (inventDimFromInventDimCombination)
    {
        forecastPurch.InventDimId = inventDimFromInventDimCombination.inventDimId;
    }

    super();

    if (defaultDate)
    {
        forecastPurch.StartDate = defaultDate;
    }

    forecastPurch.PdsCWUnitId = PdsCatchWeightItem::pdsCWUnitId(forecastPurch.ItemId);

    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    inventDim.data(InventDim::find(forecastPurch.InventDimId));

    if (defaultInventDim)
    {
        inventDim.initFromInventDim(defaultInventDim);
    }

    inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
}

public void init()
{
    QueryBuildDataSource qB;

    super();

    qB = this.query().dataSourceNo(1);

    if (calledFromBudgetPlanning)
    {
        this.initFromBudgetPlanning();
    }

    criteriaExpand_1      = qB.addRange(fieldNum(ForecastPurch,ExpandId));
    criteriaModelId       = qB.addRange(fieldNum(ForecastPurch,ModelId));
    criteriaVendGroupId   = qB.addRange(fieldNum(ForecastPurch,VendGroupId));
    criteriaVendAccountId = qB.addRange(fieldNum(ForecastPurch,VendAccountId));
    criteriaItemGroupId   = qB.addRange(fieldNum(ForecastPurch,ItemGroupId));
    criteriaItemId        = qB.addRange(fieldNum(ForecastPurch,ItemId));

    if (cwFormCtrl)
    {
        cwFormCtrl.dataSourceInitPost(forecastPurch);
    }

    qb.addOrderByField(fieldnum(ForecastPurch, StartDate), SortOrder::Descending);
}

public void executeQuery()
{
    criteriaExpand_1.value('0');

    criteriaModelId.value(       lockModelId.value()       ? queryValue(critModelId.text())       : SysQuery::valueUnlimited() );
    criteriaVendGroupId.value(   lockVendGroupId.value()   ? queryValue(critVendGroupId.text())   : SysQuery::valueUnlimited() );
    criteriaVendAccountId.value( lockVendAccountId.value() ? queryValue(critVendAccountId.text()) : SysQuery::valueUnlimited() );
    criteriaItemGroupId.value(   lockItemGroupId.value()   ? queryValue(critItemGroupId.text())   : SysQuery::valueUnlimited() );
    criteriaItemId.value(        lockItemId.value()        ? queryValue(critItemId.text())        : SysQuery::valueUnlimited() );

    super();
}

public int active()
{
    int             ret;
    boolean         canEdit = ! forecastPurch.derived();
    ForecastModel   forecastModel;

    ret = super();

    if (canEdit && forecastPurch.ModelId)
    {
        forecastModel = ForecastModel::find(HeadingSub::Heading, forecastPurch.ModelId);
        if (forecastModel && forecastModel.Blocked)
        {
            canEdit = false;
        }
    }

    this.allowEdit(canEdit);
    this.allowDelete(canEdit);

    element.updateButtons();
    this.updateForecastPurchFields();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    forecastPurchExpand_ds.executeQuery();

    this.dataSourceActivePost();

    return ret;
}

public void updateAmount()
{
    forecastPurch.Amount = forecastPurch.calcPrice2LineAmount(forecastPurch.PurchQty,true);
}

public void updateForecastPurchFields()
{
    forecastPurch_ds.object(fieldNum(ForecastPurch,Freq)).allowEdit(    forecastPurch.BudgetMap::mustEnableFreq());
    forecastPurch_ds.object(fieldNum(ForecastPurch,FreqCode)).allowEdit(forecastPurch.BudgetMap::mustEnableFreqCode());
    forecastPurch_ds.object(fieldNum(ForecastPurch,KeyId)).allowEdit(   forecastPurch.BudgetMap::mustEnableKeyId());
    forecastPurch_ds.object(fieldNum(ForecastPurch,EndDate)).allowEdit( forecastPurch.BudgetMap::mustEnableEndDate());

    forecastPurch_ds.object(fieldNum(ForecastPurch,VendGroupId)).allowEdit( !lockVendGroupId.allowEdit() || forecastPurch.VendAccountId ? false : true);
    forecastPurch_ds.object(fieldNum(ForecastPurch,ItemGroupId)).allowEdit( !lockItemGroupId.allowEdit() || forecastPurch.ItemId ? false : true);

    forecastPurch_ds.object(fieldNum(ForecastPurch,VendAccountId)).allowEdit(lockVendAccountId.allowEdit());
    forecastPurch_ds.object(fieldNum(ForecastPurch,ItemId)).allowEdit(lockItemId.allowEdit());
}

