public void init()
{
    QueryBuildDataSource    qbsForecastPurchExpand;

    super();

    qbsForecastPurchExpand = this.query().dataSourceTable(tableNum(ForecastPurch));

    criteriaReport    = qbsForecastPurchExpand.addRange(fieldNum(ForecastPurch,Report));
    criteriaExpand_2  = qbsForecastPurchExpand.addRange(fieldNum(ForecastPurch,ExpandId));

    qbsForecastPurchExpand.addSortField(fieldName2id(tableNum(ForecastPurch),fieldStr(ForecastPurch,StartDate)));
}

public void executeQuery()
{
    if ( ! forecastPurch.RecId)                     // then query must not find any records
    {
        criteriaExpand_2.value(queryValue('! 0 '));
        criteriaReport.value(queryValue(NoYes::No));
    }
    else
    {
        criteriaExpand_2.value(queryValue(forecastPurch.RecId));
        criteriaReport.value(SysQuery::valueUnlimited());
    }

    super();
}

public void clicked()
{
    if (forecastPurch.AllocateMethod !=  BudgetAllocateMethod::None)
    {
        overview_GridForecastPurchExpand.setFocus();
        ctrlLedgerCoverage.dataSource(FormDataUtil::getFormDataSource(forecastPurchExpand).name());

        if (forecastPurchExpand.CovStatus == 0)
        {
            ttsbegin;
            forecastPurchExpand.updateLedgerCov(LedgerCoverage::construct(forecastPurchExpand));
            ttscommit;
        }
    }
    else
    {
        ctrlLedgerCoverage.dataSource(FormDataUtil::getFormDataSource(forecastPurch).name());

        if (forecastPurch.CovStatus == 0)
        {
            ttsbegin;
            forecastPurch.updateLedgerCov(LedgerCoverage::construct(forecastPurch));
            ttscommit;
        }
    }

    super();
}

public void clicked()
{
    super();

    forecastPurch_ds.research();
}

