[Form]
public class FreeNotes_IT extends FormRun
{
    private container Modules = [ModuleInventCustVend::Cust, ModuleInventCustVend::Vend];
    public CustTable custTable;
    public VendTable vendTable;
    public InventTable inventTable;
    public ModuleInventCustVend actualModule;

}

/// <summary>
/// Converts <c>ModuleCustVend</c> value to <c>ModuleInventCustVend</c> value.
/// </summary>
/// <param name = "_module"><c>ModuleCustVend</c> value.</param>
/// <returns><c>ModuleInventCustVend</c> value.</returns>
public ModuleInventCustVend custVend2InventCustVend(ModuleCustVend _module)
{
    ModuleInventCustVend ret;

    switch (_module)
    {
        case ModuleCustVend::Cust:
            ret = ModuleInventCustVend::Cust;
            break;
        case ModuleCustVend::Vend:
            ret = ModuleInventCustVend::Vend;
            break;
        default:
            ret = ModuleInventCustVend::Invent;
            break;
    }
    return ret;
}

/// <summary>
/// Converts <c>ModuleInventCustVend</c> value to <c>ModuleCustVend</c> value.
/// </summary>
/// <param name = "_module"><c>ModuleInventCustVend</c> value.</param>
/// <returns><c>ModuleCustVend</c> value.</returns>
public ModuleCustVend inventCustVend2CustVend(ModuleInventCustVend _module)
{
    ModuleCustVend ret;

    switch (_module)
    {
        case ModuleInventCustVend::Cust:
            ret = ModuleCustVend::Cust;
            break;
        default:
            ret = ModuleCustVend::Vend;
            break;
    }
    return ret;
}

/// <summary>
/// Complements the standard form initialization process.
/// </summary>
/// <exception>Exception is thrown if the module value is not Cust or Vend.</exception>
public void init()
{
    container ModulesLocal = [ModuleCustVend::Cust, ModuleCustVend::Vend];

    if (element.args().parm())
    {
        if (conFind(ModulesLocal, element.args().parmEnum()))
        {
            actualModule = element.custVend2InventCustVend(element.args().parmEnum());
        }
        else
        {
            throw error(strfmt("@SYS54195", funcname()));
        }
    }
    else
    {
        actualModule = ModuleInventCustVend::Invent;
    }

    if (element.args().record())
    {
        switch (element.args().dataset())
        {
            case TableNum(InventTable):
                inventTable = element.args().record();
                actualModule = ModuleInventCustVend::Invent;
                break;

            case TableNum(CustTable):
                custTable = element.args().record();
                actualModule = ModuleInventCustVend::Cust;
                break;

            case TableNum(VendTable):
                vendTable = element.args().record();
                actualModule = ModuleInventCustVend::Vend;
                break;

            default:
                break;
        }
    }

    super();

    switch (actualModule)
    {
        case ModuleInventCustVend::Cust :
            CustVendFreeNotesHeader_OnPurchOrder.visible(false);
            CustVendFreeNotesLines_OnPurchOrder.visible(false);
            break;

        case ModuleInventCustVend::Vend:
            CustDocumentsForHeader.visible(false);
            CustDocumentsForLines.visible(false);
            break;

        default:
            CustVendFreeNotesHeader_Module.visible(true);
            CustVendFreeNotesLines_Module.visible(true);
            break;

    }

    if (inventTable)
    {
        Header.visible(false);
    }
}

/// <summary>
/// Contains shared logic for form datasources write action values validation.
/// </summary>
/// <param name = "_record">The <c>FreeNotesTable_IT</c> record to validate.</param>
/// <returns>true if the record is valid, otherwise false.</returns>
public boolean validateDatasourceWrite(FreeNotesTable_IT _record)
{
    boolean isValid = true;
    str validValue;
    str validValueForGroup;
    str validValueForTable;
    str warningMessageCode;
    str warningMessageRelation;
    TableGroupAll relationCode;
    str relation;

    if (custTable || vendTable)
    {
        validValueForTable = (custTable) ? custTable.AccountNum : vendTable.AccountNum;
        validValueForGroup = (custTable) ? custTable.FreeNotesGroup_IT : vendTable.FreeNotesGroup_IT;
        warningMessageCode = "@ExtendedItallianLocalization:AccountCodeNotAllowed";
        warningMessageRelation = "@ExtendedItallianLocalization:CustVendRelationNotAllowed";
        relationCode = _record.CustVendCode;
        relation = _record.CustVendRelation;
    }

    if (inventTable)
    {
        validValueForTable = inventTable.ItemId;
        validValueForGroup = inventTable.FreeNotesGroup_IT;
        warningMessageCode = "@ExtendedItallianLocalization:ItemCodeNotAllowed";
        warningMessageRelation = "@ExtendedItallianLocalization:ItemRelationNotAlllowed";
        relationCode = _record.ItemCode;
        relation = _record.ItemRelation;
    }

    switch (relationCode)
    {
        case TableGroupAll::Table:
            validValue = validValueForTable;
            break;
        case TableGroupAll::GroupId:
            validValue = validValueForGroup;
            break;
        case TableGroupAll::All:
            warning(warningMessageCode);
            isValid = false;
            break;
        default:
            break;
    }

    if (relation
        && validValue
        && relation != validValue)
    {
        warning(warningMessageRelation);
        isValid = false;
    }

    return isValid;
}

public void initDataSource(Query _query, str _dataSourceName, HeaderLines_IT _headerLines)
{
    const str valueTemplate = '%1,%2';


    if (custTable || vendTable || inventTable)
    {
        _query.dataSourceName(_dataSourceName).clearRanges();
        _query.dataSourceName(_dataSourceName).clearDynalinks();
        int fieldId;
        str value;

        if (custTable)
        {
            fieldId = fieldNum(FreeNotesTable_IT, CustVendRelation);
            value = strFmt(valueTemplate, custTable.AccountNum, custTable.FreeNotesGroup_IT);
        }
        else if (vendTable)
        {
            fieldId = fieldNum(FreeNotesTable_IT, CustVendRelation);
            value = strFmt(valueTemplate, vendTable.AccountNum, vendTable.FreeNotesGroup_IT);
        }
        else if (inventTable)
        {
            fieldId = fieldNum(FreeNotesTable_IT, ItemRelation);
            value = strFmt(valueTemplate, inventTable.ItemId, inventTable.FreeNotesGroup_IT);
        }

        _query.dataSourceName(_dataSourceName).addRange(fieldId).value(value);
    }

    if (conFind(Modules, actualModule))
    {
        ModuleCustVend module = element.inventCustVend2CustVend(actualModule);
        _query.dataSourceName(_dataSourceName).addRange(fieldNum(FreeNotesTable_IT, Module)).value(enum2str(module));
    }

    _query.dataSourceName(_dataSourceName).addRange(fieldNum(FreeNotesTable_IT, HeaderLines)).value(enum2str(_headerLines));
}

public void create(boolean _append = false)
{
    super(_append);

    custVendFreeNotesHeader.Restriction = DocuRestriction::External;

    if (conFind(Modules, actualModule))
    {
        ModuleCustVend module = element.inventCustVend2CustVend(actualModule);
        custVendFreeNotesHeader.Module = module;
    }

    custVendFreeNotesHeader.HeaderLines = HeaderLines_IT::Header;
    custVendFreeNotesHeader.ItemCode = TableGroupAll::All;
}

public void init()
{
    super();
    element.initDataSource(
        this.query(),
        formDataSourceStr(FreeNotes_IT, CustVendFreeNotesHeader),
        HeaderLines_IT::Header);
}

