[Form]
public class GanttWrkCtrColor extends FormRun
{
    GanttColor_WrkCtr ganttColor;
}

public void applyChanges()
{
    this.getValuesFromForm();

    ganttColor.applyChanges();

    this.enableApplyButton(false);
}

public boolean checkCaller(Object _callerObject)
{
    if (!SysDictClass::isEqualOrSuperclass(classidget(_callerObject), classnum(GanttColor_WrkCtr)))
    {
        error(strfmt("@SYS86720", element.form().name(), classstr(GanttColor_WrkCtr)));

        return false;
    }

    return true;
}

protected void getPlannedProdJobColorSchemaFromClass()
{
    if (ganttColor.parmPlannedProdJobColorScheme() == GanttColorSchemeWrkCtrPlannedProd::ActivePassive)
    {
        activePassiveColorPlannedProdJobGrp.optionValue(1);
        plannedProdJobTypeColorGrp.optionValue(0);
    }
    else if (ganttColor.parmPlannedProdJobColorScheme() == GanttColorSchemeWrkCtrPlannedProd::ProdJobType)
    {
        activePassiveColorPlannedProdJobGrp.optionValue(0);
        plannedProdJobTypeColorGrp.optionValue(1);
    }
    else
    {
        throw error("@SYS6144");
    }
}

protected void getPlannedProdJobColorSchemaFromForm()
{
    if (activePassiveColorPlannedProdJobGrp.optionValue() == 1)
    {
        ganttColor.parmPlannedProdJobColorScheme(GanttColorSchemeWrkCtrPlannedProd::ActivePassive);
    }
    else if (plannedProdJobTypeColorGrp.optionValue() == 1)
    {
        ganttColor.parmPlannedProdJobColorScheme(GanttColorSchemeWrkCtrPlannedProd::ProdJobType);
    }
    else
    {
        throw error("@SYS6144");
    }
}

protected void getProdJobColorSchemaFromForm()
{
    if (activePassiveColorProdJobGrp.optionValue() == 1)
    {
        ganttColor.parmProdJobColorScheme(GanttColorSchemeWrkCtrProd::ActivePassive);
    }
    else if (productionColorProdJobGrp.optionValue()== 1)
    {
        ganttColor.parmProdJobColorScheme(GanttColorSchemeWrkCtrProd::Production);
    }
    else if (prodJobTypeColorGrp.optionValue() == 1)
    {
        ganttColor.parmProdJobColorScheme(GanttColorSchemeWrkCtrProd::ProdJobType);
    }
    else if (prodJobStatusColorGrp.optionValue() == 1)
    {
        ganttColor.parmProdJobColorScheme(GanttColorSchemeWrkCtrProd::ProdJobStatus);
    }
    else
    {
        throw error("@SYS6144");
    }
}

protected void getProdJobColorSchemaValueFromClass()
{
    if (ganttColor.parmProdJobColorScheme() == GanttColorSchemeWrkCtrProd::ActivePassive)
    {
        activePassiveColorProdJobGrp.optionValue(1);
        productionColorProdJobGrp.optionValue(0);
        prodJobTypeColorGrp.optionValue(0);
        prodJobStatusColorGrp.optionValue(0);
    }
    else if (ganttColor.parmProdJobColorScheme() == GanttColorSchemeWrkCtrProd::Production)
    {
        activePassiveColorProdJobGrp.optionValue(0);
        productionColorProdJobGrp.optionValue(1);
        prodJobTypeColorGrp.optionValue(0);
        prodJobStatusColorGrp.optionValue(0);
    }
    else if (ganttColor.parmProdJobColorScheme() == GanttColorSchemeWrkCtrProd::ProdJobType)
    {
        activePassiveColorProdJobGrp.optionValue(0);
        productionColorProdJobGrp.optionValue(0);
        prodJobTypeColorGrp.optionValue(1);
        prodJobStatusColorGrp.optionValue(0);
    }
    else if (ganttColor.parmProdJobColorScheme() == GanttColorSchemeWrkCtrProd::ProdJobStatus)
    {
        activePassiveColorProdJobGrp.optionValue(0);
        productionColorProdJobGrp.optionValue(0);
        prodJobTypeColorGrp.optionValue(0);
        prodJobStatusColorGrp.optionValue(1);
    }
    else
    {
        throw error("@SYS6144");
    }
}

protected void getProdValuesFromForm()
{
    this.getProdJobColorSchemaFromForm();

    // job types
    ganttColor.parmProdJobTypeQueueBeforeColor(prodJob_QueueBefore.backgroundColor());
    ganttColor.parmProdJobTypeSetupColor(prodJob_Setup.backgroundColor());
    ganttColor.parmProdJobTypeProcessColor(prodJob_Process.backgroundColor());
    ganttColor.parmProdJobTypeOverlapColor(prodJob_Overlap.backgroundColor());
    ganttColor.parmProdJobTypeTransportColor(prodJob_Transport.backgroundColor());
    ganttColor.parmProdJobTypeQueueAfterColor(prodJob_QueueAfter.backgroundColor());

    // job statuses
    ganttColor.parmProdJobStatusComingColor(prodJob_Coming.backgroundColor());
    ganttColor.parmProdJobStatusWaitingColor(prodJob_Waiting.backgroundColor());
    ganttColor.parmProdJobStatusStoppedColor(prodJob_Stopped.backgroundColor());
    ganttColor.parmProdJobStatusStartedColor(prodJob_Started.backgroundColor());
    ganttColor.parmProdJobStatusCompletedColor(prodJob_Completed.backgroundColor());
}

