    this.getProjValuesFromForm();
}

public void init()
{
    Object  callerObject;

    if (!element.args() || !element.args().caller())
    {
        throw error(strfmt("@SYS84645", element.form().name()));
    }

    callerObject = element.args().caller();

    if (!this.checkCaller(callerObject))
    {
        throw Exception::Error;
    }

    ganttColor = callerObject;

    super();

    this.setCaptionByCaller();

    this.initControls();

    this.enableControls();
}

void lookupColor(FormIntControl _formIntControl)
{
    int currentColor = _formIntControl.backgroundColor();
    int selectedColor = ColorSelection::selectColor(_formIntControl, _formIntControl.backgroundColor());

    if (selectedColor != currentColor)
    {
        _formIntControl.backgroundColor(selectedColor);
        this.fieldValueChanged();
    }
}

void setCaptionByCaller()
{
    GanttClient_WrkCtr localGanttClient = ganttColor.parmGanttClient();
    str caption = "@SYS26308";
    switch (localGanttClient.parmGanttCaller())
    {
        case GanttCallerWrkCtr::WrkCtr      :

            caption += ' - ' + "@SYS10369";
            break;

        case GanttCallerWrkCtr::GanttTable  :

            caption += ' - ' + "@SYS14794";
            break;

        case GanttCallerWrkCtr::Prod        :

            caption += ' - ' + "@SYS9747";
            break;

        case GanttCallerWrkCtr::PlannedProd :

            caption += ' - ' + "@SYS12742";
            break;

        case GanttCallerWrkCtr::Proj        :

            caption += ' - ' + "@SYS50498";
            break;
    }

    element.design().caption(caption);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.fieldValueChanged();

    return ret;
}

void clicked()
{
    super();

    ganttColor.setDefaultColors();  // set default values

    element.getValuesFromClass();   // get default values

    ganttColor.getLast(); // set back the previous values

    element.fieldValueChanged();
}
