[Form]
public class GanttWrkCtrJobInfo extends FormRun
{
    GanttTmpWrkCtrJob tmpCaller;
    GanttData_WrkCtr ganttData;

}

public void init()
{
    tmpCaller = element.args().record();
    ganttData = element.args().parmObject();

    super();

    if (tmpCaller.RefType == WrkCtrCapRefType::Production)
    {
        element.design().caption(strFmt("@SYS324460", strfmt("@SYS26867", tmpCaller.JobId), tmpCaller.OprNum, tmpCaller.JobType, tmpCaller.OprPriority, tmpCaller.OprId));

        PlannedProdTabPage.visible(false);
        ReqPO_ds.linkType(DataSourceLinkTypePropertyValues::Passive);
        ReqRouteJob_ds.linkType(DataSourceLinkTypePropertyValues::Passive);
    }
    else
    {
        element.design().caption(strFmt("@SYS330953", tmpCaller.OprNum, tmpCaller.JobType, tmpCaller.OprId));

        ProductionTabPage.visible(false);
        ProdTable_ds.linkType(DataSourceLinkTypePropertyValues::Passive);
        ProdRouteJob_ds.linkType(DataSourceLinkTypePropertyValues::Passive);
    }

    if (ProdGanttApplyUserTimezoneOffsetFlight::instance().isEnabled() &&
        ProdApplyUserTimezoneOffsetFlight::instance().isEnabled())
    {
        this.showDateTimeWithUserTimezone(ProdParameters::find().ShowSchedDateTimeWithUserTimeZone == ProdShowDateTimeTimeZone::UserTimeZone);
    }
}

public void init()
{
    super();

    QueryBuildDataSource qbds = GanttTmpWrkCtrJob_ds.queryBuildDataSource();
    qbds.clearLinks();
    qbds.addRange(fieldNum(GanttTmpWrkCtrJob, RecId)).value(queryValue(tmpCaller.RecId));
}

