    qbds.addRange(fieldNum(ReqRouteJob, ReqPOId)).value(queryValue(tmpCaller.RefId));
    qbds.addRange(fieldNum(ReqRouteJob, OprNum)).value(queryValue(tmpCaller.OprNum));
