public void init()
{
    super();

    QueryBuildDataSource qbds = GanttTmpMaterials_ds.queryBuildDataSource();
    qbds.clearLinks();
    qbds.addRange(fieldNum(GanttTmpMaterials, RefId)).value(queryValue(tmpCaller.RefId));
    qbds.addRange(fieldNum(GanttTmpMaterials, RefType)).value(queryValue(tmpCaller.RefType));
    qbds.addRange(fieldNum(GanttTmpMaterials, OprNum)).value(queryValue(tmpCaller.OprNum));
    qbds.addSortField(fieldNum(GanttTmpMaterials, AvailabilityDate), SortOrder::Descending);
    qbds.addSortField(fieldNum(GanttTmpMaterials, AvailabilityTime), SortOrder::Descending);

    GanttTmpMaterials.linkPhysicalTableInstance(ganttData.parmGanttTmpMaterials());
}

/// <summary>
/// Displays whether the materials are available on the current scheduled date.
/// </summary>
/// <returns>
/// A resource identifier of the icon to show.
/// </returns>
public display container materialAvailabilityStatusImg(GanttTmpMaterials _ganttTmpMaterials)
{
    str imgSymbol;

    if (!_ganttTmpMaterials.AvailabilityDate)
    {
        imgSymbol = ImageReferenceSymbol::Help;
    }
    else
    {
        if (_ganttTmpMaterials.AvailabilityDate > ganttTmpWrkCtrJob.SchedFromDate ||
            (   _ganttTmpMaterials.AvailabilityDate == ganttTmpWrkCtrJob.SchedFromDate 
             && _ganttTmpMaterials.AvailabilityTime > ganttTmpWrkCtrJob.SchedFromTime))
        {
            imgSymbol = ImageReferenceSymbol::Warning;
        }
        else
        {
            imgSymbol = ImageReferenceSymbol::Accept;
        }
    }

    return ImageReference::constructForSymbol(imgSymbol).pack();
}
