Method.Source:
[Form]
public class GanttWrkCtrSetup extends FormRun
{
    GanttSetup_WrkCtr   ganttSetup;

}

public boolean applyChanges()
{
    this.getValuesFromForm();

    if (ganttSetup.applyChanges())
    {
        this.enableApplyButton(false);

        return true;
    }
    else
    {
        return false;
    }
}

public boolean checkCaller(Object _callerObject)
{
    if (!(_callerObject is GanttSetup_WrkCtr))
    {
        error(strfmt("@SYS86720", element.form().name(), classstr(GanttSetup_WrkCtr)));
        return false;
    }

    return true;
}

void disableControlsByCaller()
{
    GanttClient_WrkCtr localGanttClient = ganttSetup.parmGanttClient();
    switch (localGanttClient.parmGanttCaller())
    {
        case GanttCallerWrkCtr::Prod        :

            // Load production data
            loadProdJobGrp.enabled(false);
            loadProdJobGrp.visible(true);
            break;

        case GanttCallerWrkCtr::PlannedProd :

            // Load planned production data
            loadPlannedProdJobGrp.enabled(false);
            loadPlannedProdJobGrp.visible(true);

            // Plan
            plannedProdReqPlanId.enabled(false);
            plannedProdReqPlanId.visible(true);
            break;

        case GanttCallerWrkCtr::Proj        :

            // Load project data
            loadProjGrp.enabled(false);
            loadProjGrp.visible(true);
            break;
    }

    if (localGanttClient.parmLoadOnlySpecific())
    {
        loadProdJobGrp.enabled(false);
        loadPlannedProdJobGrp.enabled(false);
        loadProjGrp.enabled(false);
    }
}

public void enableControls()
{
    this.enableApplyButton(false);

    this.hideControlsByCaller();
    this.disableControlsByCaller();

    this.enableProdJobControls();
    this.enablePlannedProdJobControls();
    this.enableProjControls();

    this.enableCalendarControls();
}

void enablePlannedProdJobControls()
{
    boolean enableControls = loadPlannedProdJobGrp.optionValue();
    // column 1
    showPlannedProdJobTypesGrp.enableChilds(enableControls);

    // column 2
    showPlannedProdJobsGrp.enableChilds(enableControls);
    plannedProdJobDataLoadingGrp.enableChilds(enableControls);
}

void enableProdJobControls()
{
    boolean enableControls = loadProdJobGrp.optionValue();
    // column 1
    showProdJobTypesGrp.enableChilds(enableControls);

    // column 2
    showProdJobsGrp.enableChilds(enableControls);
    prodJobDataLoadingGrp.enableChilds(enableControls);
    prodJobOtherGrp.enableChilds(enableControls);
}

protected void getPlannedProdValuesFromClass()
{
    if (ReqParameters::find().ReqInactive)
    {
        ganttSetup.parmLoadPlannedProdJobData(NoYes::No);
        loadPlannedProdJobGrp.allowEdit(false);
        plannedProdJobTab.visible(false);
    }

    loadPlannedProdJobGrp.optionValue(ganttSetup.parmLoadPlannedProdJobData());

    plannedProdJob_QueueBefore.value(ganttSetup.parmShowPlannedProdJobTypeQueueBefore());
    plannedProdJob_Setup.value(ganttSetup.parmShowPlannedProdJobTypeSetup());
    plannedProdJob_Process.value(ganttSetup.parmShowPlannedProdJobTypeProcess());
    plannedProdJob_Overlap.value(ganttSetup.parmShowPlannedProdJobTypeOverlap());
    plannedProdJob_Transport.value(ganttSetup.parmShowPlannedProdJobTypeTransport());
    plannedProdJob_QueueAfter.value(ganttSetup.parmShowPlannedProdJobTypeQueueAfter());

    noCapResPlannedProdJobs.value(ganttSetup.parmShowPlannedProdJobsNoCapRes());

    loadEntirePlannedProdOrder.value(ganttSetup.parmLoadEntirePlannedProdOrder());

    plannedProdJobMaterialDate.value(ganttSetup.parmShowPlannedProdJobMaterialDate());

    plannedProdReqPlanId.text(ganttSetup.parmReqPlanIdPlannedProd());
}

public void getValuesFromClass()
{
    this.getGeneralValuesFromClass();
    this.getProdValuesFromClass();
    this.getPlannedProdValuesFromClass();
    this.getProjValuesFromClass();
}

public void getValuesFromForm()
{
    this.getGeneralValuesFromForm();
    this.getProdValuesFromForm();
    this.getPlannedProdValuesFromForm();
    this.getProjectValuesFromForm();
}

void hideControlsByCaller()
{
    GanttClient_WrkCtr localGanttClient = ganttSetup.parmGanttClient();
    switch (localGanttClient.parmGanttCaller())
    {
        case GanttCallerWrkCtr::Prod        :
        case GanttCallerWrkCtr::PlannedProd :
        case GanttCallerWrkCtr::Proj        :

            // Hide 'Load entire order' for production
            prodJobDataLoadingGrp.visible(false);

            // Hide 'Load entire order' for planned production
            loadEntirePlannedProdOrder.visible(false);

            // Hide 'Load entire order' for project
            projDataLoadingGrp.visible(false);

            break;
    }
}

public void init()
{
    Object  callerObject;

    if (!element.args() || !element.args().caller())
    {
        throw error(strfmt("@SYS84645", element.form().name()));
    }

    callerObject = element.args().caller();

    if (!this.checkCaller(callerObject))
    {
        throw Exception::Error;
    }

    ganttSetup = callerObject;

    super();

    this.setCaptionByCaller();

    this.initControls();

    this.enableControls();
}

void setCaptionByCaller()
{
    GanttClient_WrkCtr localGanttClient = ganttSetup.parmGanttClient();
    str caption = "@SCM:GanttChartContent";
    switch (localGanttClient.parmGanttCaller())
    {
        case GanttCallerWrkCtr::WrkCtr      :

            caption += ' - ' + "@SYS10369";
            break;

        case GanttCallerWrkCtr::GanttTable  :

            caption += ' - ' + "@SYS14794";
            break;

        case GanttCallerWrkCtr::Prod        :

            caption += ' - ' + "@SYS9747";
            break;

        case GanttCallerWrkCtr::PlannedProd :

            caption += ' - ' + "@SYS12742";
            break;

        case GanttCallerWrkCtr::Proj        :

            caption += ' - ' + "@SYS50498";
            break;
    }

    element.design().caption(caption);
}

