[Form]
public class GanttWrkCtrView extends FormRun
{
    GanttView_WrkCtr ganttView;
    private boolean isGanttWrkCtrViewLeftPaneFeatureEnabled;

}

void activateCurrentTab()
{
    if (ganttView.parmCurrentView() == GanttViewWrkCtr::OrderView)
    {
        orderViewTab.activatePage();
    }
    else if (ganttView.parmCurrentView() == GanttViewWrkCtr::ResourceView)
    {
        resourceViewTab.activatePage();
    }
    else
    {
        throw error("@SYS108494");
    }
}

public boolean applyChanges()
{
    this.getValuesFromForm();

    if (ganttView.applyChanges())
    {
        this.enableApplyButton(false);

        return true;
    }
    else
    {
        return false;
    }
}

public boolean checkCaller(Object _callerObject)
{
    #define.ganttClientMethodName('ganttClient')
    if (!SysDictClass::isEqualOrSuperclass(classidget(_callerObject), classnum(GanttView_WrkCtr)))
    {
        error(strfmt("@SYS86720", element.form().name(), classstr(GanttView_WrkCtr)));
        return false;
    }

    return true;
}

public void enableControls()
{
    this.enableApplyButton(false);

    this.enableOrderViewRefDeliveryDate();
    this.showLeftPaneViewTab();
}

public void enableOrderViewRefDeliveryDate()
{
    boolean enabled = orderViewRefIdGrp.optionValue();
    orderViewRefDeliveryDate.enabled(enabled);
}

public void getValuesFromClass()
{
    this.getOrderViewValuesFromClass();
    this.getResourceViewValuesFromClass();

    if (isGanttWrkCtrViewLeftPaneFeatureEnabled)
    {
        this.getLeftPaneValuesFromClass();
    }
}

public void getValuesFromForm()
{
    this.getOrderViewValuesFromForm();
    this.getResourceViewValuesFromForm();

    if (isGanttWrkCtrViewLeftPaneFeatureEnabled)
    {
        this.getLeftPaneValuesFromForm();
    }
}

public void init()
{
    Object callerObject;

    if (!element.args() || !element.args().caller())
    {
        throw error(strfmt("@SYS84645", element.form().name()));
    }

    callerObject = element.args().caller();

    if (!this.checkCaller(callerObject))
    {
        throw Exception::Error;
    }

    ganttView = callerObject;

    super();

    this.setCaptionByCaller();

    this.initControls();

    this.enableControls();
}

void setCaptionByCaller()
{
    GanttClient_WrkCtr localGanttClient = ganttView.parmGanttClient();
    str caption = "@SYS108523";
    switch (localGanttClient.parmGanttCaller())
    {
        case GanttCallerWrkCtr::WrkCtr:
            caption += ' - ' + "@SYS10369";
            break;

        case GanttCallerWrkCtr::GanttTable:
            caption += ' - ' + "@SYS14794";
            break;

        case GanttCallerWrkCtr::Prod:
            caption += ' - ' + "@SYS9747";
            break;

        case GanttCallerWrkCtr::PlannedProd:
            caption += ' - ' + "@SYS12742";
            break;

        case GanttCallerWrkCtr::Proj:
            caption += ' - ' + "@SYS50498";
            break;
    }

    element.design().caption(caption);
}

public void clicked()
{
    super();

    element.fieldValueChanged();

    element.enableOrderViewRefDeliveryDate();
}
