[Form]
public class GoodsInRouteEditLines_RU extends FormRun
{
    Dialog                  dialog;
    GoodsInRouteEngine_RU   updateEngine;

    InventDimCtrl_Frm_Mov   inventDimFormSetup;
    TmpFrmVirtual           tmpFrmVirtual;

    boolean                 markingAll;
}

void closeOk()
{
    dialog.updateServer();
    if (updateEngine.checkCloseDialog())
    {
        super();
    }
}

void findTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, TableId _tableId, RecId _recId)
{
    select _tmpFrmVirtual
        where _tmpFrmVirtual.TableNum   == _tableId &&
              _tmpFrmVirtual.RecordNo   == _recId;
}

void  init()
{
    if (! element.args().caller())
    {
        throw error("@GLS103799");
    }

    dialog = element.args().caller();
    updateEngine = dialog.runBase();
    tmpFrmVirtual.setTmpData(updateEngine.tmpFrmVirtual());

    super();

    element.design().caption(updateEngine.caption());

    element.setButtonAccess();
    element.updateDesign(InventDimFormDesignUpdate::Init);
}

Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

void markAll()
{
    QueryRun    queryRun = new QueryRun(custInvoiceTrans_ds.queryRun().query());
    ;

    markingAll = true;
    while (queryRun.next())
    {
        custInvoiceTrans_ds.markTrans(true, queryRun.get(tableNum(CustInvoiceTrans)), markAll.value());
    }
    markingAll = false;

    element.setButtonAccess();
}

void setButtonAccess()
{
    select firstonly tmpFrmVirtual
        where tmpFrmVirtual.NoYes
           && tmpFrmVirtual.Qty;

    okButton.enabled(tmpFrmVirtual.RecId != 0);
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    SalesLine       salesLine;
    InventMovement  movement = InventMovement::construct(salesLine);

    switch (classidget(updateEngine))
    {
        case classnum(GoodsInRouteUpdate_RU) :
            this.design().caption("@SYS4081564");
            SupplementalInstruction.text("@SYS4081565");
            correction.visible(false);
            break;
        case classnum(GoodsInRouteCancel_RU) :
            this.design().caption("@SYS4081566");
            SupplementalInstruction.text("@SYS4081567");
            correction.visible(true);
            break;
    }

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newInventTable(movement.inventTable()), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newInventTable(movement.inventTable()), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

void writeTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, TableId _tableId, RecId _recId, Qty _qty = 0, Qty _inventQty = _tmpFrmVirtual.InventQty_RU, Qty _pdsCWQty = _tmpFrmVirtual.PdsCWQty)
{
    _tmpFrmVirtual.TableNum     = _tableId;
    _tmpFrmVirtual.RecordNo     = _recId;
    _tmpFrmVirtual.Qty          = _qty;
    _tmpFrmVirtual.InventQty_RU = _inventQty;
    _tmpFrmVirtual.PdsCWQty     = _pdsCWQty;
    _tmpFrmVirtual.NoYes        = _qty != 0;
    _tmpFrmVirtual.write();
}

public void init()
{
    QueryBuildRange qbr;
    ;
    super();

    this.query().dataSourceName(this.name()).clearDynalinks();
    qbr = this.query().dataSourceName(this.name()).addRange(fieldNum(CustInvoiceJour, RecId));
    qbr.value(queryValue(updateEngine.parmCustInvoiceJour().RecId));
    qbr.status(RangeStatus::Locked);
}

private void setupEditable()
{
    CustInvoiceTrans    cursor              = this.cursor();
    boolean             isCW                = PdsGlobal::pdsIsCWItem(cursor.ItemId);
    boolean             isDeliveryExists    = custInvoiceTrans.qtyToDelivery_RU() != 0;

    custInvoiceTrans_Qty.allowEdit(!isCW && isDeliveryExists);
    SalesGroup_qty.allowEdit(!isCW && isDeliveryExists);

    CustInvoiceTrans_pdsCWProcessingQty.allowEdit(isCW && isDeliveryExists);
    pdsCWProcessingQty.allowEdit(isCW && isDeliveryExists);
}

edit Qty pdsCWProcessingQty(boolean             _set,
                            CustInvoiceTrans    _custInvoiceTrans,
                            Qty                 _qty)
{
    Qty     cwQty;
    Qty     cwQtyRemain;
    Qty     salesQty;
    Qty     inventQty;

    element.findTmpFrmVirtual(tmpFrmVirtual,
                            _custInvoiceTrans.TableId,
                            _custInvoiceTrans.RecId);
    if (tmpFrmVirtual)
    {
        cwQty = tmpFrmVirtual.PdsCWQty;
    }

    if (_set &&
        ((updateEngine.transSign() > 0 && _qty >= 0) ||
         (updateEngine.transSign() < 0 && _qty <= 0)))
    {
        _qty = decRound(_qty, UnitOfMeasure::findBySymbol(_custInvoiceTrans.pdsCWUnitId()).DecimalPrecision);

        cwQtyRemain = _custInvoiceTrans.pdsCWQtyToDelivery_RU();
        if (abs(_qty) > cwQtyRemain)
        {
            cwQty = _qty < 0 ? -cwQtyRemain : cwQtyRemain;
        }
        else
        {
            cwQty = _qty;
        }

        if (abs(cwQty) == cwQtyRemain)
        {
            salesQty  = updateEngine.transSign() * _custInvoiceTrans.qtyToDelivery_RU();
            inventQty = updateEngine.transSign() * _custInvoiceTrans.inventQtyToDelivery_RU();
        }
        else
        {
            salesQty   = _custInvoiceTrans.Qty * cwQty / _custInvoiceTrans.PdsCWQty;
            salesQty   = decRound(salesQty, UnitOfMeasure::findBySymbol(_custInvoiceTrans.SalesUnit).DecimalPrecision);

            inventQty = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(
                _custInvoiceTrans.ItemId, 
                _custInvoiceTrans.InventDimId,
                salesQty,
                _custInvoiceTrans.SalesUnit,
                InventTableModule::find(_custInvoiceTrans.ItemId, ModuleInventPurchSales::Invent).UnitId,
                NoYes::Yes);

            inventQty = updateEngine.transSign() * min(abs(inventQty), abs(_custInvoiceTrans.inventQtyToDelivery_RU()));
        }

        element.writeTmpFrmVirtual(tmpFrmVirtual,
                                   _custInvoiceTrans.TableId,
                                   _custInvoiceTrans.RecId,
                                   salesQty,
                                   inventQty,
                                   cwQty);
        element.setButtonAccess();
    }

    return cwQty;
}

display InventUnitId inventUnit()
{
    CustInvoiceTrans    cursor = this.cursor();
    return InventTableModule::find(cursor.ItemId, ModuleInventPurchSales::Invent).UnitId;
}

edit Qty inventProcessingQty(boolean            _set,
                              CustInvoiceTrans  _custInvoiceTrans,
                              Qty               _qty)
{
    Qty qty;
    Qty qtyRemain;

    element.findTmpFrmVirtual(tmpFrmVirtual,
                            _custInvoiceTrans.TableId,
                            _custInvoiceTrans.RecId);
    if (tmpFrmVirtual)
    {
        qty = tmpFrmVirtual.InventQty_RU;
    }

    if (_set &&
        ((updateEngine.transSign() > 0 && _qty >= 0) ||
         (updateEngine.transSign() < 0 && _qty <= 0)))
    {
        _qty = decRound(_qty, UnitOfMeasure::findBySymbol(
                                InventTableModule::find(_custInvoiceTrans.ItemId, ModuleInventPurchSales::Invent).UnitId).DecimalPrecision);

        qtyRemain = _custInvoiceTrans.inventQtyToDelivery_RU();
        if (abs(_qty) > qtyRemain)
        {
            qty = _qty < 0 ? -qtyRemain : qtyRemain;
        }
        else
        {
            qty = _qty;
        }

        element.writeTmpFrmVirtual(tmpFrmVirtual,
                                   _custInvoiceTrans.TableId,
                                   _custInvoiceTrans.RecId,
                                   tmpFrmVirtual.Qty,
                                   qty);
        element.setButtonAccess();
    }

    return qty;
}

public void init()
{
    super();

    custInvoiceTrans_ds.cacheAddMethod(tableMethodStr(CustInvoiceTrans, qtyToDelivery_RU));
}

public int active()
{
    int ret;
    int inventQtyPrecision;

    ret = super();

    this.setupEditable();

    inventQtyPrecision = UnitOfMeasure::findBySymbol(InventTableModule::find(CustInvoiceTrans.ItemId, ModuleInventPurchSales::Invent).UnitId).DecimalPrecision;
    inventQtyToDelivery.noOfDecimals(inventQtyPrecision);
    inventProcessingQty.noOfDecimals(inventQtyPrecision);

    return ret;
}

edit SalesInvoicedQty qty(boolean           _set,
                          CustInvoiceTrans  _custInvoiceTrans,
                          SalesQty          _qty)
{
    SalesQty    qty;
    SalesQty    qtyRemain;
    Qty         inventQty;
    Qty         inventQtyRemain;
    ;
    _qty = decRound(_qty, UnitOfMeasure::unitOfMeasureDecimalPrecision(UnitOfMeasure::unitOfMeasureIdBySymbol(_custInvoiceTrans.SalesUnit)));
    element.findTmpFrmVirtual(tmpFrmVirtual,
                              _custInvoiceTrans.TableId,
                              _custInvoiceTrans.RecId);
    if (tmpFrmVirtual)
    {
        qty = tmpFrmVirtual.Qty;
    }

    if (_set &&
        ((updateEngine.transSign() > 0 && _qty >= 0) ||
         (updateEngine.transSign() < 0 && _qty <= 0)))
    {
        qtyRemain = _custInvoiceTrans.qtyToDelivery_RU();
        if (abs(_qty) > qtyRemain)
        {
            qty = _qty < 0 ? -qtyRemain : qtyRemain;
        }
        else
        {
            qty = _qty;
        }

        inventQty = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(
            _custInvoiceTrans.ItemId,
            _custInvoiceTrans.InventDimId,
            qty,
            _custInvoiceTrans.SalesUnit,
            InventTableModule::find(_custInvoiceTrans.ItemId, ModuleInventPurchSales::Invent).UnitId,
            NoYes::Yes);

        inventQtyRemain = _custInvoiceTrans.inventQtyToDelivery_RU();
        if (abs(inventQty) > inventQtyRemain)
        {
            inventQty = inventQty < 0 ? -inventQtyRemain : inventQtyRemain;
        }

        element.writeTmpFrmVirtual(tmpFrmVirtual,
                                   _custInvoiceTrans.TableId,
                                   _custInvoiceTrans.RecId,
                                   qty,
                                   inventQty);

        element.setButtonAccess();
    }

    return qty;
}

edit NoYes markTrans(boolean            _set,
                     CustInvoiceTrans   _custInvoiceTrans,
                     NoYes              _mark)
{
    NoYes   mark;

    element.findTmpFrmVirtual(tmpFrmVirtual,
                              _custInvoiceTrans.TableId,
                              _custInvoiceTrans.RecId);

    if (tmpFrmVirtual && tmpFrmVirtual.NoYes == NoYes::Yes)
    {
        mark = NoYes::Yes;
    }

    if (_set)
    {
        if (_mark && _custInvoiceTrans.qtyToDelivery_RU())
        {
            element.writeTmpFrmVirtual(tmpFrmVirtual,
                                       _custInvoiceTrans.TableId,
                                       _custInvoiceTrans.RecId,
                                       tmpFrmVirtual.Qty);
            mark = NoYes::Yes;
        }
        else
        {
            if (tmpFrmVirtual)
            {
                tmpFrmVirtual.NoYes = NoYes::No;
                tmpFrmVirtual.update();
            }
            mark = NoYes::No;
        }

        if (! markingAll)
        {
            element.setButtonAccess();
        }
    }

    return mark;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.markAll();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    updateEngine.parmCorrect(correct.value());

    return ret;
}

