/// <summary>
/// Provides the custom lookup logic for foreign keys to the <c>HcmPositionForecastCompensationGrid</c> table.
/// </summary>
/// <param name = "_formReferenceControl">The <c>FormReferenceControl</c> form object for which to resolve the reference.</param>
/// <returns>The <c>HcmPositionForecastCompensationGrid</c> table record.</returns>
public HcmPositionForecastCompensationGrid lookupCompensationGrid(FormReferenceControl _formReferenceControl)
{
    Args args = new Args(formStr(HcmPositionForecastCompensationGridLookup));
    args.caller(this);
    args.record(hcmPositionForecastScenario.data());

    FormRun formRun = ClassFactory::formRunClassOnClient(args);
    formRun.init();
    _formReferenceControl.performFormLookup(formRun);

    HcmPositionForecastCompensationGrid positionForecastCompensationGrid = formRun.selectRecordModeSelectedRecord();

    return positionForecastCompensationGrid;
}

public void enableDisableBudgetFields()
{
    boolean allowOverride = hcmBudgetPurposeType.PositionOverride != HcmBudgetPositionOverride::DisallowOverride;
    boolean percentageOnly = hcmPositionForecastBudgetAcctLine.IsPercentageBased;

    hcmPositionForecastBudgetAcctLine_ds.object(fieldNum(HcmPositionForecastBudgetAcctLine, AmountCur)).allowEdit(allowOverride && !percentageOnly);

    hcmPositionForecastBudgetAcctLine_ds.object(fieldNum(HcmPositionForecastBudgetAcctLine, PercentageOfDefaultBasisAmount)).allowEdit(allowOverride && percentageOnly);

    hcmPositionForecastBudgetAcctLine_ds.object(fieldNum(HcmPositionForecastBudgetAcctLine, IsLineLockedForRecalculation)).allowEdit(allowOverride);

    hcmPositionForecastBudgetAcctLine_ds.object(fieldNum(HcmPositionForecastBudgetAcctLine, BudgetAmountCur)).allowEdit(false);

    // has to be done on control level, field enabling does not work
    referenceGroup.allowEdit(hcmPositionForecastBudgetAcctLine.RecId == 0);
}

public void init()
{
    int controlCount;

    FormStringControl formStringControl;
    #DEFINE.PositionIdControlName('_PositionId')
    #DEFINE.LegalEntityControlName('_DataArea')
    #DEFINE.PlanningProcessControlName('_Name')
    #DEFINE.PlanScenarioControlName('_Name')
    #DEFINE.LevelControlName('_HcmCompensationLevel_CompensationLevelId')
    FormDataSource fds;

    super();

    selectedLegalEntityDataAreaId = CompanyInfo::findRecId(CompanyInfo::current()).DataArea;

    for (controlCount=1; controlCount <= hcmPositionForecast_Position.controlCount(); controlCount++)
    {
        // The metadata properties of a reference group's controls are not accessable from the AOT.
        if (hcmPositionForecast_Position.controlNum(controlCount).name() == (hcmPositionForecast_Position.name() + #PositionIdControlName))
        {
            // Find the form control and add it the fast tab summary.
            formStringControl = hcmPositionForecast_Position.controlNum(controlCount);
            formStringControl.fastTabSummary(true);
        }
    }

    for (controlCount=1; controlCount <= hcmPositionForecastScenario_LegalEntity.controlCount(); controlCount++)
    {
        // The metadata properties of a reference group's controls are not accessable from the AOT.
        if (hcmPositionForecastScenario_LegalEntity.controlNum(controlCount).name() == (hcmPositionForecastScenario_LegalEntity.name() + #LegalEntityControlName))
        {
            // Find the form control and add it the fast tab summary.
            formStringControl = hcmPositionForecastScenario_LegalEntity.controlNum(controlCount);
            formStringControl.fastTabSummary(true);
        }
    }

    for (controlCount=1; controlCount <= hcmForecastScenario_BudgetPlanningProcess.controlCount(); controlCount++)
    {
        // The metadata properties of a reference group's controls are not accessable from the AOT.
        if (hcmForecastScenario_BudgetPlanningProcess.controlNum(controlCount).name() == (hcmForecastScenario_BudgetPlanningProcess.name() + #PlanningProcessControlName))
        {
            // Find the form control and add it the fast tab summary.
            formStringControl = hcmForecastScenario_BudgetPlanningProcess.controlNum(controlCount);
            formStringControl.fastTabSummary(true);
        }
    }

    for (controlCount=1; controlCount <= hcmForecastScenario_BudgetPlanScenario.controlCount(); controlCount++)
    {
        // The metadata properties of a reference group's controls are not accessable from the AOT.
        if (hcmForecastScenario_BudgetPlanScenario.controlNum(controlCount).name() == (hcmForecastScenario_BudgetPlanScenario.name() + #PlanScenarioControlName))
        {
            // Find the form control and add it the fast tab summary.
            formStringControl = hcmForecastScenario_BudgetPlanScenario.controlNum(controlCount);
            formStringControl.fastTabSummary(true);
        }
    }

    for (controlCount=1; controlCount <= lookupLevelAndStep.controlCount(); controlCount++)
    {
        // The metadata properties of a reference group's controls are not accessable from the AOT.
        if (lookupLevelAndStep.controlNum(controlCount).name() == (lookupLevelAndStep.name() + #LevelControlName))
        {
            // Find the form control and add it the fast tab summary.
            formStringControl = lookupLevelAndStep.controlNum(controlCount);
            formStringControl.fastTabSummary(true);
        }
    }

    if (!element.args().caller() || element.args().caller().name() != formStr(HcmPositionForecast))
    {
        // Set the default sort order on the Position Id field from the foreign key reference group control.
        // This not required when the caller is the list page because it is already passing a "sorted" query.
        fds = hcmPositionForecast_Position.referenceDataSource();
        fds.queryBuildDataSource().addOrderByField(fieldNum(HcmPosition, PositionId));
    }

    element.addQueryFilters();
}

/// <summary>
/// Provides a custom lookup of those <c>OMOperatingUnit</c> records that are
/// of the specified <c>OMOperatingUnitType</c> type.
/// </summary>
/// <param name="_formReferenceControl">
/// The <c>FormReferenceControl</c> for which the lookup being created by this method is servicing.
/// </param>
/// <param name="_type">
/// A <c>OMOperatingUnitType</c> value that indicates what type discriminator to use by which the
/// <c>OMOperatingUnit</c> table is filtered.
/// </param>
/// <param name="_name">
/// A string specifying a user-entered name that is ambiguous and must be resolved by the lookup; optional.
/// </param>
/// <returns>
/// A <c>OmOperatingUnit</c> record that was selected by the user.
/// </returns>
/// <remarks>
/// This lookup helper method also assists in resolving duplicate names when surrogate key replacement
/// cannot do so automatically.
/// </remarks>
public OMOperatingUnit operatingUnitLookup(
        FormReferenceControl _formReferenceControl,
        OMOperatingUnitType _type,
        str 100 _name = '')
{
    OMOperatingUnit             selectedRecord;
    SysReferenceTableLookup     sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(OMOperatingUnit), _formReferenceControl, true);
    Query                       lookupQuery;
    QueryBuildDataSource        ouQbds;

    sysTableLookup.addLookupfield(fieldNum(OMOperatingUnit, Name));
    sysTableLookup.addLookupfield(fieldNum(OMOperatingUnit, NameAlias));
    sysTableLookup.addLookupfield(fieldNum(OMOperatingUnit, omOperatingUnitType));

    // Create a custom Query that filters to show only a specific OMOperatingUnitType
    lookupQuery = new Query();
    ouQbds = lookupQuery.addDataSource(tableNum(OMOperatingUnit));
    ouQbds.fields().addField(fieldNum(OMOperatingUnit, Name));
    ouQbds.fields().addField(fieldNum(OMOperatingUnit, NameAlias));
    ouQbds.fields().addField(fieldNum(OMOperatingUnit, omOperatingUnitType));
    ouQbds.addRange(fieldNum(OMOperatingUnit, omOperatingUnitType)).value(enum2str(_type));

    // If a name is specified then it is being called in a duplicate resolution scenario, so filter
    // on the name as well
    if (_name)
    {
        // Filter to the specified name to help resolve duplicates
        ouQbds.addRange(fieldNum(OMOperatingUnit, Name)).value(_name);
    }

    sysTableLookup.parmQuery(lookupQuery);
    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

