/// <summary>
/// Updates the header title based upon the values of the component fields
/// data in them.
/// </summary>
public void updateTitleField()
{
    if (hcmPositionForecast.RecId != 0)
    {
        titleField.text(HcmPosition::find(hcmPositionForecast.Position).PositionId);
        if (BudgetLinkPositionToMultipleForecastDetailsFlight::instance().isEnabled())
        {
            if (hcmPositionForecastDetailV2.Description)
            {
                titleField.text(titleField.text() + ' : ' + hcmPositionForecastDetailV2.Description);
            }
        }
        else
        {
            if (hcmPositionForecastDetail.Description)
            {
                titleField.text(titleField.text() + ' : ' + hcmPositionForecastDetail.Description);
            }
        }
    }
    else
    {
        titleField.text('');
    }
}

/// <summary>
/// Saves the last modification information for the forecast position in the <c>HcmPositionForecastLastModified</c> table and
/// updates the status flag on the <c>HcmPositionForecast</c> form.
/// </summary>
/// <param name="_notifyForm">
/// The flag to notify the form when the record is saved.
/// </param>
public void updateLastModifyByPositionForecast(boolean _notifyForm = false)
{
    // Update the last modified date and time.
    if (hcmPositionForecastLastModified.RecId)
    {
        hcmPositionForecastLastModified_ds.forceWrite(true);
    }
    else
    {
        HcmPositionForecastLastModified.PositionForecastScenario = HcmPositionForecastScenario.RecId;
    }

    if (_notifyForm == true)
    {
        hcmPositionForecastLastModified_ds.leaveRecord();
    }
}

public int task(int _taskId)
{
    #Task
    int taskCompleted;

    switch (_taskId)
    {
        case #taskEditRecord:

            taskCompleted = super(_taskId);
            element.enableDisableBudgetFields();
            break;

        case #taskSwitchToDetailsView:

            hcmPositionForecastBulkEdit.visible(false);
            taskCompleted = super(_taskId);
            break;

        case #taskSwitchToGridView:

            hcmPositionForecastBulkEdit.visible(true);
            taskCompleted = super(_taskId);
            break;
        default:
            taskCompleted = super(_taskId);
    }

    return taskCompleted;
}

/// <summary>
/// Adds <c>PositionId</c>, <c>WorkerName</c>, <c>TitleId</c> and <c>Name</c> field associated with <c>HcmPosition</c>, <c>HcmPositionForecast</c>,
/// <c>HcmTitle</c>, <c>BudgetPlanningProcess</c>, <c>BudgetPlanScenario</c> and <c>OMOperatingUnit</c> table respectively as filters
/// to the query of the <c>HcmPositionForecast</c> form.
/// </summary>
public void addQueryFilters()
{
    QueryBuildDataSource qbdsBudgetPlanningProcess;
    QueryBuildDataSource qbdsBudgetPlanScenario;
    QueryBuildDataSource qbdsHcmJob;
    QueryBuildDataSource qbdsOMOperatingUnit;
    QueryBuildDataSource qbdsHcmTitle;
    QueryBuildDataSource qbdsHcmPositionForecastScenario;
    QueryBuildDataSource qbdsHcmPosition;

    qbdsBudgetPlanningProcess = HcmForecastScenario_ds.query().datasourceTable(tableNum(BudgetPlanningProcess));
    qbdsBudgetPlanScenario = HcmForecastScenario_ds.query().datasourceTable(tableNum(BudgetPlanScenario));

    qbdsHcmPositionForecastScenario = HcmPositionForecastScenario_ds.queryBuildDataSource();
    qbdsHcmPosition = HcmPositionForecast_DS.query().datasourceTable(tableNum(HcmPosition));

    if (BudgetLinkPositionToMultipleForecastDetailsFlight::instance().isEnabled())
    {
        qbdsOMOperatingUnit = HcmPositionForecastDetailV2_ds.query().datasourceTable(tableNum(OMOperatingUnit));
        qbdsHcmTitle = HcmPositionForecastDetailV2_ds.query().datasourceTable(tableNum(HcmTitle));
        qbdsHcmJob = HcmJobV2_ds.queryBuildDataSource();

        HcmPositionForecastDetailV2_ds.query().addQueryFilter(qbdsHcmTitle, fieldStr(HcmTitle, TitleID));
        HcmPositionForecastDetailV2_ds.query().addQueryFilter(qbdsOMOperatingUnit, fieldStr(OMOperatingUnit, Name));
        HcmJobV2_ds.query().addQueryFilter(qbdsHcmJob, fieldStr(HcmJob, JobId));
    }
    else
    {
        qbdsOMOperatingUnit = HcmPositionForecastDetail_ds.query().datasourceTable(tableNum(OMOperatingUnit));
        qbdsHcmTitle = HcmPositionForecastDetail_ds.query().datasourceTable(tableNum(HcmTitle));
        qbdsHcmJob = HcmJob_ds.queryBuildDataSource();

        HcmPositionForecastDetail_ds.query().addQueryFilter(qbdsHcmTitle, fieldStr(HcmTitle, TitleID));
        HcmPositionForecastDetail_ds.query().addQueryFilter(qbdsOMOperatingUnit, fieldStr(OMOperatingUnit, Name));
        HcmJob_ds.query().addQueryFilter(qbdsHcmJob, fieldStr(HcmJob, JobId));
    }

    HcmForecastScenario_ds.query().addQueryFilter(qbdsBudgetPlanningProcess, fieldStr(BudgetPlanningProcess, Name));
    HcmForecastScenario_ds.query().addQueryFilter(qbdsBudgetPlanScenario, fieldStr(BudgetPlanScenario, Name));

    HcmPositionForecast_DS.query().addQueryFilter(qbdsHcmPosition, fieldStr(HcmPosition, positionId));
    HcmPositionForecastScenario_ds.query().addQueryFilter(qbdsHcmPositionForecastScenario, fieldStr(HcmPositionForecastScenario, WorkerName));

}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (BudgetLinkPositionToMultipleForecastDetailsFlight::instance().isEnabled())
    {
        if (!hcmPositionForecastDetailV2.Job)
        {
            ret = checkFailed(strFmt("@SYS84753", fieldPName(HcmPositionForecastDetailV2, Job)));
        }
    }
    else
    {
        if (!hcmPositionForecastDetail.Job)
        {
            ret = checkFailed(strFmt("@SYS84753", fieldPName(HcmPositionForecastDetail, Job)));
        }
    }

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.updateTitleField();

    return ret;
}

public void init()
{
    super();

    if ((element.args().menuItemName() && element.args().menuItemName() != menuItemDisplayStr(HcmPositionForecastList))
        && element.args().record() is HcmPositionForecast)
    {
        // Filter by a particular forecast position
        QueryBuildDataSource forecastPositionDataSource = this.queryBuildDataSource();
        forecastPositionDataSource.addRange(fieldNum(HcmPositionForecast, RecId)).value(SysQuery::value(element.args().record().RecId));
    }
    else if (element.args().menuItemName()
                && element.args().menuItemName() != menuItemDisplayStr(HcmPositionForecastList)
                && element.args().record() is HcmPositionForecastScenario)
    {
        HcmPositionForecastScenario forecastScenario = element.args().record() as HcmPositionForecastScenario;
        HcmPositionForecast_DS.queryBuildDataSource().addRange(fieldNum(HcmPositionForecast, RecId)).value(SysQuery::value(forecastScenario.PositionForecast));
    }
}

public void written()
{
    super();

    // Update the last modified date and time.
    element.updateLastModifyByPositionForecast();

    // Update hcmPositionForecastDetailV2 table if hcmPositionForecastDetail is updated.
    HcmForecastScenario hcmForecastScenariolocal;
    HcmPositionForecastScenario hcmPositionForecastScenariolocal;
    HcmPositionForecastDetailV2 hcmPositionForecastDetailV2local;

    hcmForecastScenariolocal = HcmForecastScenario::findByPlanningProcessAndScenario(hcmForecastScenario.BudgetPlanningProcess,
                                                                                hcmForecastScenario.BudgetPlanScenario);
    hcmPositionForecastScenariolocal = HcmPositionForecastScenario::findByPositionForecastBudgetPlanScenario(hcmPositionForecastDetail.PositionForecast,hcmForecastScenariolocal.RecId);

    hcmPositionForecastDetailV2local = HcmPositionForecastDetailV2::findByPositionForecastScenerio(hcmPositionForecastScenariolocal.RecId,hcmPositionForecastDetail.PositionForecast);

    if (hcmPositionForecastDetailV2local.RecId != 0)
    {
        hcmPositionForecastDetailV2local.selectForUpdate(true);
        ttsbegin;

        if (hcmPositionForecastDetail.orig().Description != hcmPositionForecastDetail.Description)
        {                   
            hcmPositionForecastDetailV2local.Description = hcmPositionForecastDetail.Description;         
        }

        if (hcmPositionForecastDetail.orig().Department != hcmPositionForecastDetail.Department)
        {
            hcmPositionForecastDetailV2local.Department = hcmPositionForecastDetail.Department;
        }

        if (hcmPositionForecastDetail.orig().Job != hcmPositionForecastDetail.Job)
        {
            hcmPositionForecastDetailV2local.Job = hcmPositionForecastDetail.Job;
        }

        if (hcmPositionForecastDetail.orig().Title != hcmPositionForecastDetail.Title)
        {
            hcmPositionForecastDetailV2local.Title = hcmPositionForecastDetail.Title;
        }

        if (hcmPositionForecastDetail.orig().PositionType != hcmPositionForecastDetail.PositionType)
        {
            hcmPositionForecastDetailV2local.PositionType = hcmPositionForecastDetail.PositionType;
        }

        if (hcmPositionForecastDetail.orig().CompLocation != hcmPositionForecastDetail.CompLocation)
        {
            hcmPositionForecastDetailV2local.CompLocation = hcmPositionForecastDetail.CompLocation;
        }

        hcmPositionForecastDetailV2local.doUpdate();
        ttscommit;
    }
}

