public void deleted()
{
    super();

    if (HcmPositionForecastBudgetAcctLine::existByPositionForecastScenario(hcmPositionForecastScenario.RecId, hcmPositionForecastBudgetAcctLine.RecId))
    {
        hcmPositionForecastLastModified.markPositionAsDirty(HcmPositionForecastScenarioState::PositionDirty);
        markDirty = true;
    }
    else
    {    // Clean the state if no account line exists.
        hcmPositionForecastLastModified.markPositionAsDirty(HcmPositionForecastScenarioState::Clean);
        markDirty = false;
    }

    element.updateLastModifyByPositionForecast(true);

    element.updateMessageBar();
}

public boolean validateWrite()
{
    boolean isValid;

    isValid = super();

    if (isValid && hcmPositionForecastBudgetAcctLine.RecId == 0)
    {
        markDirty = true;
        element.updateMessageBar();
    }

    return isValid;
}

display HRCCompRefPointDescription displayStep(HcmPositionForecastBudgetAcctLine _positionForecastBudgetAcctLine)
{
    HRCCompRefPointDescription refPointDescription = '';
    HcmBudgetPurposeType costElement;

    costElement = HcmBudgetPurposeType::find((HcmBudgetPurposeTypeDetail::find(_positionForecastBudgetAcctLine.BudgetPurposeTypeDetail)).BudgetPurposeType);

    if (costElement.CostElementType == HcmBudgetCostElementType::Earning)
    {
        refPointDescription = hcmPositionForecastScenario.displayStep();
    }

    return refPointDescription;
}

display HRCCompRefPointDescription displayStepGrid(HcmPositionForecastBudgetAcctLine _positionForecastBudgetAcctLine)
{
    HRCCompRefPointDescription refPointDescription = '';
    if (_positionForecastBudgetAcctLine.PositionForecastCompensationGrid > 0)
    {
        refPointDescription = HcmPositionForecastFormUtility::getStepDescription(_positionForecastBudgetAcctLine.PositionForecastCompensationGrid);
    }
    return refPointDescription;
}

display AmountCur displayFactor(HcmPositionForecastBudgetAcctLine _positionForecastBudgetAcctLine)
{
    return (_positionForecastBudgetAcctLine.ExpirationDate - _positionForecastBudgetAcctLine.EffectiveDate + 1) /365;
}

display NumberOfDays displayNumberDays(HcmPositionForecastBudgetAcctLine _positionForecastBudgetAcctLine)
{
    return _positionForecastBudgetAcctLine.ExpirationDate - _positionForecastBudgetAcctLine.EffectiveDate + 1;
}

