/// <summary>
/// Gets the budget cycle dates and pass it to the resolve reference budget method of the <c>HcmBudgetPurposeTypeDetail</c> table.
/// </summary>
/// <param name="_formReferenceControl">
/// The budget purpose type detail reference control.
/// </param>
/// <returns>
/// returns budget purpose type detail record.
/// </returns>
public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    ValidFromDate   startDate;
    ValidToDate     endDate;
    container       dateRangeContainer = HcmPositionForecastScenario::getBudgetPurposeTypeRange(hcmPositionForecastScenario.getUserTimeZoneActivationDate(), hcmPositionForecastScenario.getUserTimeZoneRetirementDate(),hcmForecastScenario.BudgetProcessStartDate, hcmForecastScenario.BudgetProcessEndDate);

    startDate = conPeek(dateRangeContainer, 1);
    endDate   = conPeek(dateRangeContainer, 2);

    return HcmBudgetPurposeTypeDetail::resolveReferenceBudgetPTDByCompanyDate(_formReferenceControl,
                legalEntityForPlanningProcess.RecId,
                startDate,
                endDate);
}

