public void modified()
{
    HcmPositionForecastBudgetAcctLine hcmPositionForecastBudgetAcctLineOld =  hcmPositionForecastBudgetAcctLine.orig();

    super();

    if (hcmPositionForecastBudgetAcctLineOld.EffectiveDate != hcmPositionForecastBudgetAcctLine.EffectiveDate)
    {
        ///
        // If the Effective date is outside of budget cycle then default to the maximum date of budget start date,
        // position activation date and budget purpose effective date.
        ///
        if (hcmPositionForecastBudgetAcctLine.EffectiveDate < hcmForecastScenario.BudgetProcessStartDate)
        {
            hcmPositionForecastBudgetAcctLine.EffectiveDate = max(hcmPositionForecastScenario.getPositionForecastDateRangeStartDate(), hcmBudgetPurposeTypeDetail.EffectiveDate);
        }

        markDirty = true;
        element.updateMessageBar();
    }
}

public boolean validate()
{
    boolean isValid;

    isValid = super() && hcmPositionForecastBudgetAcctLine.validateBudgetPurposeDates();

    return isValid;
}

public void modified()
{
    HcmPositionForecastBudgetAcctLine hcmPositionForecastBudgetAcctLineOld =  hcmPositionForecastBudgetAcctLine.orig();

    super();

    if (hcmPositionForecastBudgetAcctLineOld.ExpirationDate != hcmPositionForecastBudgetAcctLine.ExpirationDate)
    {
        ///
        // If the Expiration date is outside of budget cycle then default to the minimum date of budget end date,
        // position expiration date and budget purpose expiration date.
        ///
        if (hcmPositionForecastBudgetAcctLine.ExpirationDate > hcmForecastScenario.BudgetProcessEndDate)
        {
            hcmPositionForecastBudgetAcctLine.ExpirationDate = min(hcmPositionForecastScenario.getPositionForecastDateRangeEndDate(), hcmBudgetPurposeTypeDetail.ExpirationDate);
        }

        markDirty = true;
        element.updateMessageBar();
    }
}

public boolean validate()
{
    boolean isValid;

    isValid = super() && hcmPositionForecastBudgetAcctLine.validateBudgetPurposeDates();

    return isValid;
}

