public void written()
{
    HcmPositionForecastScenarioUtil hcmPositionForecastScenarioUtil = new HcmPositionForecastScenarioUtil();

    super();

    if (markDirty)
    {
        // hcmPositionForecastLastModified buffer is used instead of hcmPositionForecastScenario to get recid of PositionForecastScenario
        // because hcmPositionForecastScenario buffer was pointing to incorrect record.

        if (HcmPositionForecastBudgetAcctLine::existByPositionForecastScenario(hcmPositionForecastLastModified.PositionForecastScenario))
        {
            hcmPositionForecastScenarioUtil.markRecordAsDirty(hcmPositionForecastLastModified.PositionForecastScenario);
        }
        else
        {
            hcmPositionForecastScenarioUtil.markRecordAsDirty(hcmPositionForecastLastModified.PositionForecastScenario, true);
        }

        // Refresh the data source with the updated values.
        hcmPositionForecastLastModified_ds.research(true);
        // Reset the dirty flag because position forecast state is already updated.
        markDirty = false;
    }
}

public void processCostElement()
{
    if (hcmPositionForecastScenario.validateWrite())
    {
        markDirty = true;
        hcmPositionForecastScenario_ds.updateBudgetAcctLines();
        element.updateMessageBar();
    }
}

public int active()
{
    int activeSuccessful = false;

    activeSuccessful = super();

    legalEntityForPlanningProcess = CompanyInfo::findRecId(hcmPositionForecastScenario.LegalEntity);

    if (hcmPositionForecastScenario.CompensationGroupId)
    {
        hcmPositionForecastCompensationGroup = HcmPositionForecastCompensationGroup::findByCompensationGrpIdDataAreaId(hcmPositionForecastScenario.CompensationGroupId,
                                                                                                                       hcmPositionForecastScenario.CompensationGroupDataAreaId);
    }

    if (activeSuccessful)
    {
        // Enable the level/step control only when the compensation group is assigned to the forecast position.
        hcmPositionForecastScenario_ds.object(fieldNum(HcmPositionForecastScenario, PositionForecastCompensationGrid)).enabled(hcmPositionForecastCompensationGroup.RecId != 0);
    }

    hcmPositionForecastBudgetAmountTotalsViewDetail_ds.query().dataSourceTable(tableNum(HcmPositionForecastBudgetAmountTotalsView)).addRange(fieldNum(HcmPositionForecastBudgetAmountTotalsView, PositionForecastScenario)).value(queryValue(hcmPositionForecastScenario.RecId));
    hcmPositionForecastBudgetAmountTotalsViewDetail_ds.executeQuery();

    return activeSuccessful;
}

public void initValue()
{
    super();
    hcmPositionForecastBudgetAcctLine.PositionForecastScenario = hcmPositionForecastScenario.RecId;
}

public void updateBudgetAcctLines()
{
    ValidToDate positionActivationDate;
    ValidFromDate positionRetirementDate;

    positionActivationDate = hcmPositionForecastScenario.getUserTimeZoneActivationDate();
    positionRetirementDate = hcmPositionForecastScenario.getUserTimeZoneRetirementDate();

    try
    {
        HcmPositionForecastBudgetAcctLine::updatePositionBudgetCostElementDates(hcmPositionForecastScenario.RecId, positionActivationDate , positionRetirementDate);

        if (hcmPositionForecastScenario.PositionForecastCompensationGrid != 0)
        {
            // Update the grid id based on new duration
            hcmPositionForecastScenario.setCompensationGridByDuration();
        }
    }
    catch
    {
        // Reverting to the orignal value in case of any failure
        hcmPositionForecastScenario.ActivationDateTime = hcmPositionForecastScenario.orig().ActivationDateTime;
        hcmPositionForecastScenario.RetirementDateTime = hcmPositionForecastScenario.orig().RetirementDateTime;
    }

    hcmPositionForecastBudgetAcctLine_ds.research(true);

    //
    // to ensure that changes are save to database so that if user modifies the date next time
    // the lines get processed based on the correct values.
    //
    hcmPositionForecastScenario_ds.leaveRecord();
}

