public void init()
{
    super();

    if (element.args().caller()
        && element.args().caller().name() == formStr(BudgetPlan)
        && element.args().parm())
    {
        // Filter by a particular budget planning process if the caller is a budget plan
        QueryBuildDataSource forecastScenarioDataSource = this.queryBuildDataSource();
        forecastScenarioDataSource.addRange(fieldNum(HcmForecastScenario, BudgetPlanningProcess)).value(SysQuery::value(element.args().parm()));
    }
    else if (element.args().menuItemName()
                && element.args().menuItemName() != menuItemDisplayStr(HcmPositionForecastList)
                && element.args().record() is HcmPositionForecastScenario)
    {
        HcmPositionForecastScenario forecastScenario = element.args().record() as HcmPositionForecastScenario;
        HcmForecastScenario_DS.queryBuildDataSource().addRange(fieldNum(HcmForecastScenario, RecId)).value(SysQuery::value(forecastScenario.ForecastScenario));
    }
}

/// <summary>
/// Calculates the sum of all the budget cost elements on a given forecast position.
/// </summary>
/// <param name = "_hcmPositionForecastBudgetAmountTotalsView">
/// A buffer of type <c>HcmPositionForecastBudgetAmountTotalsView</c> that contains the sum of each of the cost element types.
/// </param>
/// <returns>
/// The sum of all the budget cost elements on a given forecast position.
/// </returns>
public display HcmBudgetAmountCur displayTotalBudgetAmount(HcmPositionForecastBudgetAmountTotalsView _hcmPositionForecastBudgetAmountTotalsView)
{
    return _hcmPositionForecastBudgetAmountTotalsView.TotalEarnings +
           _hcmPositionForecastBudgetAmountTotalsView.TotalBenefits +
           _hcmPositionForecastBudgetAmountTotalsView.TotalTaxes +
           _hcmPositionForecastBudgetAmountTotalsView.TotalOthers;
}

