public boolean modified()
{
    CompanyInfo legalEntity = CompanyInfo::find(false, hcmPositionForecastScenario.LegalEntity);
    HcmPositionForecastCompensationGroup positionForecastCompensationGroup;
    boolean modifiedSuccessful = true;

    positionForecastCompensationGroup = HcmPositionForecastCompensationGroup::findByCompensationGrpIdDataAreaId(this.text(), legalEntity.DataArea);

    if (HcmPositionForecastFormUtility::handleCompensationGroupModified(hcmPositionForecastScenario.RecId,
                                                                        positionForecastCompensationGroup.CompensationGroupId,
                                                                        legalEntity.DataArea))
    {
        if (positionForecastCompensationGroup.RecId != 0)
        {
            hcmPositionForecastScenario.CompensationGroupDataAreaId = positionForecastCompensationGroup.DataAreaId;
        }

        modifiedSuccessful = super();

        if (!modifiedSuccessful)
        {
            return false;
        }

        // If the user selects "Ok" on the dialog, then remove the level and step details as well.
        hcmPositionForecastScenario.PositionForecastCompensationGrid = 0;

        hcmPositionForecastLastModified_ds.leaveRecord(true);
        hcmPositionForecastScenario_ds.object(fieldNum(HcmPositionForecastScenario, PositionForecastCompensationGrid)).enabled(true);

        hcmPositionForecastScenario.setNextIncreaseDate();
    }

    hcmPositionForecastScenario_ds.leaveRecord(true);

    return modifiedSuccessful;
}

/// <summary>
/// Displays a confirmation dialog to allow the user to confirm or abort the change before committing it.
/// </summary>
/// <returns>
/// true if the record is allowed to be modified; otherwise, false.
/// </returns>
/// <remarks>
/// This method displays a confirmation dialog to allow the user to confirm or abort the change before committing it,
/// because changing the Compensation Level is potentially a destructive action that can cause the budget cost elements
/// associated with the active position forecast to be deleted or updated.
/// </remarks>
public boolean modified()
{
    HcmPositionAssignCostElement_CompGrid hcmPositionForecastAssignCostElement;
    RefRecId originalCompensationGrid = hcmPositionForecastScenario.PositionForecastCompensationGrid;

    // Stores the value this method will return: true by default; otherwise it holds the value returned by super().
    boolean validState = true;

    if (originalCompensationGrid != this.value()
        && hcmPositionForecastScenario.validateClearAndUpdateEarningCosts(legalEntityForPlanningProcess.RecId, this.value()))
    {
        // If the user has changed the Compensation Level and confirms the change, then continue.
        // Otherwise, no further action is taken. super() is not called, and true is returned to indicate that the control
        // still has a valid value. In Rainier, control.modified should return false only if the control's value is invalid.
        // So, even though the change was aborted, we still return true.

        if (hcmPositionForecastCompensationGroup.HcmBudgetPurposeType == 0
            && this.value() != 0)
        {
            // Abort if the Compensation Group does not have an Earning Cost Element set, and the user did not clear the Compensation Level.
            checkFailed("@SYS4005777");
        }
        else
        {
            // Since the user has confirmed the change, super() is called to perform DataSource and table level validations.
            validState = super();

            if (validState)
            {
                // If the validations succeed, then the change is committed and true is returned.
                // Otherwise, no further action is taken and false is returned.

                if (this.value())
                {
                    // If the user did not clear the Compensation Level, then save the new Compensation Level now.
                    // The cost element update process requires the that new Compensation Level is saved before running.
                    hcmPositionForecastScenario_ds.leaveRecord(true);
                }

                // Run the cost element update process.
                hcmPositionForecastAssignCostElement = HcmPositionForecastAssignCostElement::newAssignEarningCostFromCompGroup(hcmPositionForecastScenario.RecId,
                    hcmPositionForecastCompensationGroup.RecId);

                hcmPositionForecastAssignCostElement.parmIsLevelStepExisted(this.value() != 0);
                hcmPositionForecastAssignCostElement.runForecastPositionCostAssignments();

                if (hcmPositionForecastAssignCostElement.parmEarningCostAssigned() == false)
                {
                    // If the cost element update process failed, then revert the Compensation Level back to its original value
                    // from before the user made a change.
                    hcmPositionForecastScenario.PositionForecastCompensationGrid = originalCompensationGrid;
                }

                // Refresh the grid of cost elements to reflect the update.
                hcmPositionForecastBudgetAcctLine_ds.research(true);
            }
        }
    }
    else
    {
        // If the user doesn't want to make the changes, the values for the compensation step and level will be
        // reverted back to the original values.
        this.value(originalCompensationGrid);
    }

    return validState;
}

