public void closeOk()
{
    #Task
    FormRun callerForm;

    HcmPositionForecastAssignCostElement hcmPositionForecastAssignCostElement;

    hcmPositionForecastAssignCostElement = HcmPositionForecastAssignCostElement::newAssignCostForMultiplePosition(menuItemName,
                                                                                                                  hcmBudgetPurposeType,
                                                                                                                  hcmTmpPositionForecastAssignCostElement);

    hcmPositionForecastAssignCostElement.runForecastPositionCostAssignments();

    if (element.args().caller() != null && element.args().caller().name() == formStr(HcmBudgetPurposeType))
    {
        callerForm = element.args().caller() as FormRun;
        callerForm.task(#taskF5);
    }

    super();
}

/// <summary>
/// Determine the control belong to the <c>SelectedPositionsGrid</c> Grid of the Form or not.
/// </summary>
/// <param name="_formControl">
/// The form control to validate.
/// </param>
/// <returns>
/// true, if the control belong to the <c>SelectedPositionsGrid</c> Grid; otherwise, false.
/// </returns>
private boolean filterDisabled(FormControl _formControl)
{
    FormControlId         parentFormControlId;

    if (_formControl.parentControl())
    {
        if (_formControl.parentControl().type() == FormControlType::ReferenceGroup)
        {
            parentFormControlId = _formControl.parentControl().parentControl().id();
        }
        else
        {
            parentFormControlId = _formControl.parentControl().id();
        }
    }
    return parentFormControlId == selectedPositionsGrid.id();
}

public void init()
{
    menuItemName = this.args().menuItemName();

    if (!this.args().record() ||
        this.args().record().TableId != tableNum(HcmBudgetPurposeType))
    {
        throw error(strFmt("@SYS19096", this.name(), tableStr(HcmBudgetPurposeType)));
    }

    hcmBudgetPurposeType = this.args().record() as HcmBudgetPurposeType;

    super();

    switch (menuItemName)
    {
        case menuitemDisplayStr(HcmAssignToForecastPosition):
            instruction.text("@SYP4861025");
            this.design().caption("@SYS4005562");
            assignButton.text("@GLS70684");
            break;

        case menuitemDisplayStr(HcmUpdateToForecastPosition):
            instruction.text("@SYS4005713");
            this.design().caption("@SYS4005712");
            assignButton.text("@SYS4005714");
            assignButton.helpText("@SYS4005715");
            break;

        default:
            break;
    }

    headingRecordLabel.text(strFmt("@SYS4005566", hcmBudgetPurposeType.Purpose));
}

public int task(int _taskId)
{
    int ret;

    if (element.filterDisabled(element.selectedControl()))
    {
        //
        // Disable the filter capability of the control, if the control belongs
        // to the lower grid
        //
        switch (_taskId)
        {
            // Suppress CTRL-F3, advanced filtering.
            case 2837:
            // Suppress filter by field.
            case 2843:
            // Suppress filter by selection.
            case 2844:
            // Suppress CTRL-G, grid filtering.
            case 2855:
                ret = false;
                break;

            default:
                ret = super(_taskId);
                break;
        }
    }
    else
    {
        ret = super(_taskId);
    }

    return ret;
}

