public void init()
{
    QueryBuildDataSource qbdsHcmPositionForecastBudgetAcctLine;
    QueryBuildDataSource qbdsHcmBudgetPurposeTypeDetail;

    //add range here for filtering the position against the LE of the budget cost element details
    container budgetCostElementLegalEntities;
    str             range = '';
    super();

    budgetCostElementLegalEntities = HcmBudgetPurposeTypeDetail::getLegalEntitiesByBudgetPurposeType(hcmBudgetPurposeType.RecId);

    range = SysOperationHelper::convertMultiSelectedValueString(budgetCostElementLegalEntities);
    range = con2Str(budgetCostElementLegalEntities, ",");
    hcmPositionForecastScenario_ds.queryBuildDataSource().addRange(fieldNum(HcmPositionForecastScenario, LegalEntity)).value(range);

    if (this.formRun().args().menuItemName() == menuitemDisplayStr(HcmUpdateToForecastPosition))
    {
        qbdsHcmPositionForecastBudgetAcctLine = this.query().dataSourceName(this.name()).addDataSource(tableNum(HcmPositionForecastBudgetAcctLine));
        qbdsHcmPositionForecastBudgetAcctLine.joinMode(JoinMode::ExistsJoin);
        qbdsHcmPositionForecastBudgetAcctLine.relations(true);
        qbdsHcmBudgetPurposeTypeDetail = qbdsHcmPositionForecastBudgetAcctLine.addDataSource(tableNum(HcmBudgetPurposeTypeDetail));
        qbdsHcmBudgetPurposeTypeDetail.relations(true);
        qbdsHcmBudgetPurposeTypeDetail.addRange(fieldNum(HcmBudgetPurposeTypeDetail,BudgetPurposeType)).value(strFmt('%1',hcmBudgetPurposeType.RecId));
    }
}

public void clicked()
{
    HcmPositionForecastScenario positionForecastScenario;

    super();

    if (hcmPositionForecastScenario_ds.anyMarked())
    {
        // Get the references to the marked positions.
        positionForecastScenario = hcmPositionForecastScenario_ds.getFirst(true);

        while (positionForecastScenario)
        {
            hcmTmpPositionForecastAssignCostElement_ds.insertTempPositionForecastRecord(positionForecastScenario);
            positionForecastScenario = hcmPositionForecastScenario_ds.getNext();
        }
    }
    else
    {
        hcmTmpPositionForecastAssignCostElement_ds.insertTempPositionForecastRecord(hcmPositionForecastScenario_ds.cursor());
    }

    hcmTmpPositionForecastAssignCostElement_ds.executeQuery();
    hcmPositionForecastScenario_ds.executeQuery();

    assignButton.enabled(hcmTmpPositionForecastAssignCostElement.RecId);
}

public void clicked()
{
    HcmTmpPositionForecastAssignCostElement tmpPositionForecastAssignCostElement;

    super();

    if (hcmTmpPositionForecastAssignCostElement_ds.anyMarked())
    {
        // Get the references to the marked positions.
        tmpPositionForecastAssignCostElement = hcmTmpPositionForecastAssignCostElement_ds.getFirst(true);
        ttsbegin;
        while (tmpPositionForecastAssignCostElement)
        {
            hcmTmpPositionForecastAssignCostElement_ds.removeTempPositionForecastRecords(tmpPositionForecastAssignCostElement);

            tmpPositionForecastAssignCostElement = hcmTmpPositionForecastAssignCostElement_ds.getNext();
        }
        ttscommit;
    }
    else
    {
        hcmTmpPositionForecastAssignCostElement_ds.removeTempPositionForecastRecords(hcmTmpPositionForecastAssignCostElement_ds.cursor());
    }

    hcmTmpPositionForecastAssignCostElement_ds.executeQuery();
    hcmPositionForecastScenario_ds.executeQuery();

    assignButton.enabled(hcmTmpPositionForecastAssignCostElement.RecId);
}
