void init()
{
    super();

    if (EmplParameters_RU::find().AdvanceHolderSorted)
    {
        employeeTable_RU_ds.query().clearOrderBy();

        employeeTable_RU_ds.query().dataSourceTable(tableNum(EmployeeTable_RU)).addOrderByField(
            fieldNum(EmployeeTable_RU, AdvHolder), SortOrder::Descending);

        hcmWorker_ds.query().dataSourceTable(tableNum(HcmWorker)).addOrderByField(
            fieldNum(HcmWorker, PersonnelNumber), SortOrder::Ascending);
    }

    if (element.args().caller() && element.args().dataset() && element.args().dataset() == tableNum(InventBuyerGroup))
    {
        employeeTable_RU_ds.allowEdit(false);
    }
}

/// <summary>
/// Find PayHRMorganizationId through employee id.
/// </summary>
/// <param name = "_employeeTable">Get the record in Employee table.</param>
/// <returns>Retrun the PayHRMOrganizationId.</returns>
display RpayHRMOrganizationId rPayHRMOrganizationId(EmployeeTable_RU _employeeTable)
{
    return RHRMEmplTable::find(_employeeTable.EmployeeId).PayHRMOrganizationId;
}

/// <summary>
/// Find TitleId through employee id.
/// </summary>
/// <param name = "_employeeTable">Get the record in Employee table.</param>
/// <returns>return the TitleId.</returns>
display HCMTitleId rPayTitleId(EmployeeTable_RU _employeeTable)
{
    return HcmTitle::find(RHRMEmplTable::find(_employeeTable.EmployeeId).HcmTitle).TitleId;
}

public int active()
{
    int ret = super();

    element.enableHeaderActions();

    return ret;
}

public void write()
{
    //due to inner join write() is called even though AllowEdit=No, that may cause 'access denied' error on lowered privileged sessions
    if (hasTableAccess(tableNum(HcmEmployment), AccessType::Edit))
    {
        super();
    }
}

public void jumpRef()
{
    Args args = new Args();
    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(HcmWorker), MenuItemType::Display);
    super();
    args.lookupRecord(HcmWorker::findByPersonnelNumber(hcmWorker.PersonnelNumber));
    menuFunction.run(args);
}
