/// <summary>
/// Closes the intercompany setup form.
/// </summary>
/// <remarks>
/// If the intercompany trading relation has been changed the calling datasource is reread.
/// </remarks>
public void close()
{
    interCompanyTradingSetupForm.formMethodClose();

    super();
}

public void init()
{
    interCompanyTradingSetupForm = InterCompanyTradingSetupForm::newFormRun(this);

    super();
}

/// <summary>
/// Initializes the <c>InterCompanyTradingPartner</c> table datasource.
/// </summary>
public void init()
{
    super();

    VendTable vendTable;
    DataAreaId vendorDataAreaId;

    if (element.args().record().TableId == tableNum(vendTable))
    {
        vendTable = element.args().record();
        vendorDataAreaId = vendTable.company();
    }
    else
    {
        Common buffer = element.args().record();

        DictTable dictTable = new DictTable(buffer.TableId);

        if (dictTable.tableGroup() == TableGroup::Staging)
        {
            DictField vendDataAreaIdField = interCompanyTradingSetupForm.formDataSourceGetFieldFromEdtTypeNum(buffer, extendedTypeNum(DataAreaId));
            if (vendDataAreaIdField)
            {
                vendorDataAreaId = buffer.(vendDataAreaIdField.id());

                DictField vendAccountField = interCompanyTradingSetupForm.formDataSourceGetFieldFromEdtTypeNum(buffer, extendedTypeNum(VendAccount));
                if (vendAccountField)
                {
                    changecompany(vendorDataAreaId)
                    {
                        vendTable = VendTable::find(buffer.(vendAccountField.id()));
                    }
                }
            }
        }
    }

    this.query().dataSourceName(this.name()).addDynalink(fieldNum(InterCompanyTradingPartner, VendorParty),
                                                         vendTable,
                                                         fieldNum(VendTable, Party));
    this.query().dataSourceName(this.name()).addRange(fieldNum(InterCompanyTradingPartner, VendorDataAreaId)).value(queryValue(vendorDataAreaId));
}

/// <summary>
/// Initializes the newly created <c>InterCompanyTradingPartner</c> table record.
/// </summary>
public void initValue()
{
    super();

    interCompanyTradingPartner.TradingPartnerType = InterCompanyTradingPartnerType::Vendor;
    interCompanyTradingPartner.VendorDataAreaId   = curext();
}

/// <summary>
/// Initializes the newly created <c>InterCompanyTradingRelation</c> table record.
/// </summary>
private void initRelationValues()
{
    interCompanyTradingRelation.InterCompanyTradingVendor   = interCompanyTradingPartner.RecId;
    interCompanyTradingRelation.InterCompanyTradingCustomer = interCompanyTradingPartnerCustomer.RecId;
}

/// <summary>
/// Checks whether the record in the <c>InterCompanyTradingRelation</c> table datasource can be written to the database.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
public boolean validateWrite()
{
    boolean     ret;

    if (!interCompanyTradingRelation.RecId)
    {
        interCompanyTradingPartner_ds.write();
        interCompanyTradingPartnerCustomer_ds.write();
    }

    this.initRelationValues();

    if (InterCompanyTradingRelation::existOpenAgreements(intercompanyTradingRelation))
    {
        if (interCompanyTradingRelation.Active == NoYes::Yes)
        {
            if (Box::okCancel(strFmt("@SYS342790",
                                        interCompanyTradingPartnerCustomer.CustomerDataAreaId,
                                        interCompanyTradingPartnerCustomer.custAccount(),
                                        interCompanyTradingPartner.VendorDataAreaId,
                                        interCompanyTradingPartner.vendAccount()), DialogButton::Cancel) == DialogButton::Cancel)
            {
                return false;
            }
        }
        else
        {
            if (Box::okCancel("@SYS334937", DialogButton::Cancel) == DialogButton::Cancel)
            {
                return false;
            }
        }
    }

    ret = super();

    return ret;
}

/// <summary>
/// Initializes the <c>InterCompanyTradingRelation</c> table datasource.
/// </summary>
public void init()
{
    QueryBuildDataSource    datasource;

    super();

    datasource = this.query().dataSourceName(this.name());
    datasource.relations(false);

    datasource.clearLinks();
    datasource.addLink(fieldNum(InterCompanyTradingPartner, RecId),
                       fieldNum(InterCompanyTradingRelation, InterCompanyTradingVendor));
}

/// <summary>
/// Initializes the <c>InterCompanyTradingPartner</c> table datasource.
/// </summary>
public void init()
{
    QueryBuildDataSource    datasource;
    QueryBuildRange         range;

    super();

    datasource = this.query().dataSourceName(this.name());
    datasource.relations(false);

    datasource.addLink(fieldNum(InterCompanyTradingRelation, InterCompanyTradingCustomer),
                       fieldNum(InterCompanyTradingPartner, RecId));

    range = datasource.addRange(fieldNum(InterCompanyTradingPartner, TradingPartnerType));
    range.status(RangeStatus::Hidden);
    range.value(queryValue(InterCompanyTradingPartnerType::Customer));
}

