}

/// <summary>
/// Gets and sets the <c>CustAccount</c>.
/// </summary>
/// <param name="_set">
/// true if the customer ID is set; otherwise, false.
/// </param>
/// <param name="_interCompanyTradingPartner">
/// The <c>InterCompanyTradingPartner</c> record for which to get or set the customer reference.
/// </param>
/// <param name="_custAccount">
/// The new customer ID when <paramref name="_set"/> is true.
/// </param>
/// <returns>
/// The customer ID of the record in the <c>CustTable</c> table.
/// </returns>
public edit CustAccount editCustomerAccount(boolean                       _set,
                                            InterCompanyTradingPartner    _interCompanyTradingPartner,
                                            CustAccount                   _custAccount)
{
    CustTable       custTable;

    if (_set)
    {
        if (_custAccount && _interCompanyTradingPartner.CustomerDataAreaId)
        {
            changecompany (_interCompanyTradingPartner.CustomerDataAreaId)
            {
                custTable = CustTable::find(_custAccount);
                _interCompanyTradingPartner = InterCompanyTradingPartner::findOrCreateCustomer(custTable.AccountNum,
                                                                                               _interCompanyTradingPartner.CustomerDataAreaId);

                interCompanyTradingPartnerCustomer.data(_interCompanyTradingPartner);
                interCompanyTradingPartner_ds.refresh();

                interCompanyTradingRelation.InterCompanyTradingCustomer = interCompanyTradingPartnerCustomer.RecId;
                interCompanyTradingRelation_ds.refresh();
            }
        }
    }
    else
    {
        _custAccount = _interCompanyTradingPartner.custTable().AccountNum;
    }

    return _custAccount;
}

