{
    boolean ret;

    if (!aifEndpointActionValueMapExt_RUCust.AifEndpointActionValueMap)
    {
        interCompanyTradingValueMapCustomer_DS.write();
        aifEndpointActionValueMapExt_RUCust.AifEndpointActionValueMap = interCompanyTradingValueMapCustomer.RecId;
        aifEndpointActionValueMapExt_RUCust.setDataArea();
    }

    ret = super();

    return ret;
}

