/// <summary>
/// Removes relationship between customer and vendor <c>InterCompanyTradingPartner</c> and refreshes the form.
/// </summary>
public void clicked()
{
    if (Box::yesNo("@SCM:IntercompanyTradingRelationshipRemoval", DialogButton::No) == DialogButton::Yes)
    {
        interCompanyTradingSetupForm.buttonRemoveRelationship(interCompanyTradingPartnerCustomer,
                                                              interCompanyTradingPartner);

        interCompanyTradingPartner_ds.research();

        element.close();
        Box::info("@SCM:InterCompanyTradingRelationSetupRemoveRelationSuccessCaption");
    }
}

