void enableButtons()
{
    boolean enableButtons;

    if (custInterest_ds.getFirst())
    {
        enableButtons = true;
    }

    newInterestCodeVersion.enabled(enableButtons);

    //Enable/Disable any non-new related buttons
    if (custInterest.RecId)
    {
        enableButtons = true;
    }
    else
    {
        enableButtons = false;
    }

    newInterestCodeVersion.enabled(enableButtons);
    DeleteInterestCode.enabled(enableButtons);
    custInterestLanguage.enabled(enableButtons);
    enableButtons = false;

    if (custInterest.RecId && custInterest_ds.getFirst() && sysDateEffectiveDataManager.canActivate(custInterestVersion)
        && !sysDateEffectiveDataManager.existActiveVersion(custInterest.RecId))
    {
        enableButtons = true;
    }

    activateMenuItemButton.enabled(enableButtons);
    enableButtons = false;

    if (earningsCustInterestFee_ds.getFirst())
    {
        enableButtons = true;
    }

    earningsRangesMenuItemButton.enabled(enableButtons && (earningsCustInterestVersionDetail.InterestByRange != CustInterestByRange::None));
    earningsTxt.enabled(enableButtons);
    enableButtons = false;

    if (paymentsCustInterestFee_ds.getFirst())
    {
        enableButtons = true;
    }

    paymentsRangesMenuItemButton.enabled(enableButtons && (paymentsCustInterestVersionDetail.InterestByRange != CustInterestByRange::None));

    //The following code/controls related to the 'Payments' tab of the AR interest
    //code setup have been commented out instead of being removed due to
    //uncertainity surrounding the previous use of the functionality being removed.
    //At this time, it appears that the removed functionality is not used in AR interest
    //calculation or any other module. Future features may decide to implement the
    //functionality contained in the commented out code if suitable business needs exist.

    //paymentsTxt.enabled(enableButton);

    this.enablePaymCalendarFields();
    paymentsTxt.visible(false);
}

void enableFeeAccount()
{
    earningsCustInterestFee_Fee.allowEdit(interestFee_FeeAccount.valueStr() ? true: false);
}

void enablePaymCalendarFields()
{
    RefRecId countryRegionId;
    boolean stateHolidayExists;

    if (paymCalendarCustInterest.PaymCalendar)
    {
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, InterestCalculationStart)).enabled(true);
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IsBusinessDaysGracePeriodApplicable)).enabled(true);
        countryRegionId = PaymCalendarCountryRegion::findByPaymCalendar(paymCalendarCustInterest.PaymCalendar).CountryRegion;

        if (countryRegionId)
        {
            stateHolidayExists = StateHoliday::existsForCountryRegion(countryRegionId);

            if (stateHolidayExists)
            {
                paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IncludeStateHoliday)).enabled(true);
            }
        }

        if (!stateHolidayExists)
        {
            paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IncludeStateHoliday)).setValue(false);
            paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IncludeStateHoliday)).enabled(false);
        }
    }
    else
    {
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IncludeStateHoliday)).setValue(false);
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, InterestCalculationStart)).setValue(PaymCalendarInterestCalculationStart::DueDate);
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IsBusinessDaysGracePeriodApplicable)).setValue(false);
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IncludeStateHoliday)).enabled(false);
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, InterestCalculationStart)).enabled(false);
        paymCalendarCustInterest_ds.object(fieldNum(PaymCalendarCustInterest, IsBusinessDaysGracePeriodApplicable)).enabled(false);
    }
    paymCalendarCustInterest_ds.refresh();
}

