public void init()
{
    super();

    if (!isConfigurationkeyEnabled(configurationKeyNum(PaymCalendar)))
    {
        //remove the join to the DS
        paymCalendarCustInterest_DS.allowCreate(NoYes::No);
        paymCalendarCustInterest_DS.insertIfEmpty(NoYes::No);
        paymCalendarCustInterest_DS.linkType(QueryDataLinkType::None);
    }

    sysDateEffectiveDataManager = SysDateEffectiveDataManager::construct(SysDateEffectiveSetupType::CustInterest);
    sysDateEffectiveDataManager.setMode(ValidTimeStateUpdate::Correction);

    dateEffectivenessPaneController = DateEffectivenessPaneController::constructWithForm(element, custInterest_ds);

    element.displayExpirationStatus();

    element.updateDesign();
}

