/// Updates the UI control according to business rule.
/// </summary>
public void updateDesign()
{
    if (custInterest.CustInterestType == CustInterestType::MultipleRate)
    {
        custInterest_DebitInterestByrange.visible(false);
        custInterest_DebitPercentageAmount.visible(false);
        earningsCustInterestFee_InterestValue.enabled(false);
        earningsRangesMenuItemButton.visible(false);
        custInterest_DebitInterestPercent.visible(true);
        custInterest_DebitInterestPercent.skip(false);

        custInterest_CreditInterestByrange.visible(false);
        custInterest_CreditPercentageAmount.visible(false);
        paymentsCustInterestFee_InterestValue.visible(false);
        paymentsRangesMenuItemButton.visible(false);
        custInterest_CreditInterestPercent.visible(true);

        if (CustInterestVersion::numberOfDateRecords(custInterest.InterestCode) > 1)
        {
            custInterest_InvoicePer.allowEdit(false);
            custInterest_InvoiceCalculate.allowEdit(false);
            custInterest_PaymentPer.allowEdit(false);
            custInterest_PaymentCalculate.allowEdit(false);
        }
    }

    if (custInterest.CustInterestType == CustInterestType::SingleRate)
    {
        custInterestVersion_GraceDays.allowEdit(true);
        custInterest_InvoicePer.allowEdit(true);
        custInterest_InvoiceCalculate.allowEdit(true);
        custInterest_PaymentPer.allowEdit(true);
        custInterest_PaymentCalculate.allowEdit(true);
        custInterest_DebitInterestByrange.visible(true);
        custInterest_DebitPercentageAmount.visible(true);
        custInterest_CreditInterestByrange.visible(true);
        custInterest_CreditPercentageAmount.visible(true);
        earningsRangesMenuItemButton.visible(true);
        earningsCustInterestFee_InterestValue.visible(true);
        paymentsRangesMenuItemButton.visible(true);
        paymentsCustInterestFee_InterestValue.visible(true);

        if (earningsCustInterestVersionDetail.InterestByRange == CustInterestByRange::None)
        {
            custInterest_DebitInterestPercent.enabled( true );
            earningsCustInterestFee_InterestValue.enabled( true );

            if (earningsCustInterestVersionDetail.InterestCalcType == CustInterestCalcType::Amount)
            {
                custInterest_DebitInterestPercent.visible(false);
                earningsCustInterestFee_InterestValue.visible(true);
                earningsCustInterestFee_MinimumInterestAmount.allowEdit(false);
                earningsCustInterestFee.MinimumInterestAmount = 0.00;
                earningsCustInterestFee_MaximumInterestAmount.allowEdit(false);
                earningsCustInterestFee.MaximumInterestAmount = 0.00;
                earningsCustInterestFee_ds.refresh();
            }
            else
            {
                custInterest_DebitInterestPercent.visible(true);
                earningsCustInterestFee_InterestValue.visible(false);
                earningsCustInterestFee_MinimumInterestAmount.allowEdit(true);
                earningsCustInterestFee_MaximumInterestAmount.allowEdit(true);
            }
        }

        else
        {
            custInterest_DebitInterestPercent.visible( false );
            earningsCustInterestFee_InterestValue.visible( false );
        }

        if ( paymentsCustInterestVersionDetail.InterestByRange == CustInterestByRange::None )
        {
            custInterest_CreditInterestPercent.enabled( true );
            paymentsCustInterestFee_InterestValue.enabled( true );

            if (paymentsCustInterestVersionDetail.InterestCalcType == CustInterestCalcType::Amount)
            {
                custInterest_CreditInterestPercent.visible(false);
                paymentsCustInterestFee_InterestValue.visible(true);
            }
            else
            {
                custInterest_CreditInterestPercent.visible(true);
                paymentsCustInterestFee_InterestValue.visible(false);
            }
        }

        else
        {
            custInterest_CreditInterestPercent.visible( false );
            paymentsCustInterestFee_InterestValue.visible( false );
        }
    }

    if (CustInterestVersion::numberOfDateRecords(custInterest.InterestCode) > 1)
    {
        custInterest_CustInterestType.allowEdit(false);
    }

    else
    {
        custInterest_InterestCode.allowEdit(true);
        custInterest_CustInterestType.allowEdit(true);
        custInterest_Txt.allowEdit(true);
    }
    // <GEEPL>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        this.updateDesign_PL();
    }
    // </GEEPL>

    element.enableFeeAccount();
    element.enableButtons();
}

