public boolean validateWrite()
{
    boolean shouldCallSuperToValidate = true;

    // When creating a new interest record, it is necessary to determine if future or past versions
    // exist with the same interest code. If so, special handling is needed to handle collisions.

    if (!custInterest)
    {
        CustInterest localCustInterest = CustInterest::findByInterestCode(custInterest.InterestCode);

        if (localCustInterest.RecId && !sysDateEffectiveDataManager.existActiveVersion(localCustInterest.RecId))
        {
            if (sysDateEffectiveDataManager.existFutureVersion(localCustInterest.RecId))
            {
                this.handleFutureVersionOptions(localCustInterest);
                shouldCallSuperToValidate = false;
            }
            else if (sysDateEffectiveDataManager.existPastVersion(localCustInterest.RecId))
            {
                this.handlePastVersionOptions(localCustInterest);
                shouldCallSuperToValidate = false;
            }
        }
    }

    return shouldCallSuperToValidate && super();
}

private void handleFutureVersionOptions(CustInterest _existingCustInterest)
{
    SysDateEffectiveVersion futureRecord = sysDateEffectiveDataManager.findFutureVersionByParentId(_existingCustInterest.RecId, true);

    Args args = new Args(formStr(SysDateEffectiveFutureVersionOptions));
    args.caller(element);
    args.parm(date2StrUsr(futureRecord.ValidFrom, DateFlags::None));

    FormRun formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        ISysDateEffectiveFutureVersionOptions formSysDateEffectiveFutureVersionOptions = formRun as ISysDateEffectiveFutureVersionOptions;

        if (formSysDateEffectiveFutureVersionOptions)
        {
            if (formSysDateEffectiveFutureVersionOptions.parmModifyExistingRecord() == NoYes::Yes)
            {
                SysDateEffectiveDataManagerBase::activateVersion(futureRecord);
                custInterest_ds.delete();
                custInterest_ds.executeQuery();
            }

            if (formSysDateEffectiveFutureVersionOptions.parmCreateNewRecord() == NoYes::Yes)
            {
                CustInterestVersion existingInterestVersion = futureRecord;
                CustInterestVersion newInterestVersion;

                ttsbegin;
                newInterestVersion.ValidFrom = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
                newInterestVersion.ValidTo = existingInterestVersion.ValidFrom - 1;
                newInterestVersion.CustInterest = existingInterestVersion.CustInterest;
                newInterestVersion.insert();

                sysDateEffectiveDataManager.copy(existingInterestVersion.RecId, newInterestVersion.RecId);
                ttscommit;

                custInterest_ds.delete();
                custInterest_ds.executeQuery();
            }
        }
    }
}

private void handlePastVersionOptions(CustInterest _existingCustInterest)
{
    SysDateEffectiveVersion pastRecord = sysDateEffectiveDataManager.findPastVersionByParentId(_existingCustInterest.RecId, true);

    Args args = new Args(formStr(SysDateEffectivePastVersionOptions));
    args.caller(element);
    args.parm(date2StrUsr(pastRecord.ValidTo, DateFlags::None));

    FormRun formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        ISysDateEffectivePastVersionOptions formSysDateEffectivePastVersionOptions = formRun as ISysDateEffectivePastVersionOptions;

        if (formSysDateEffectivePastVersionOptions)
        {
            if (formSysDateEffectivePastVersionOptions.parmActivateExistingRecord() == NoYes::Yes)
            {
                SysDateEffectiveDataManagerBase::activateVersion(pastRecord);
                custInterest_ds.delete();
                custInterest_ds.executeQuery();
            }

            if (formSysDateEffectivePastVersionOptions.parmCreateNewRecord() == NoYes::Yes)
            {
                CustInterestVersion existingInterestVersion = pastRecord;
                CustInterestVersion newInterestVersion;

                ttsbegin;
                newInterestVersion.ValidFrom = pastRecord.ValidTo + 1;
                newInterestVersion.ValidTo = maxdate();
                newInterestVersion.CustInterest = existingInterestVersion.CustInterest;
                newInterestVersion.insert();

                sysDateEffectiveDataManager.copy(existingInterestVersion.RecId, newInterestVersion.RecId);
                ttscommit;

                custInterest_ds.delete();
                custInterest_ds.executeQuery();
            }
        }
    }
}

