display str displayExpirationStatus(CustInterestVersion _custInterestVersion)
{
    str expirationStatus;

    if (_custInterestVersion.ValidTo < DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
    {
        expirationStatus = '@AccountsReceivable:CustInterestExpirationStatusExpired';
    }
    else if (_custInterestVersion.ValidFrom > DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
    {
        expirationStatus = '@AccountsReceivable:CustInterestExpirationStatusFuture';
    }
    else
    {
        expirationStatus = '@AccountsReceivable:CustInterestExpirationStatusActive';
    }

    return expirationStatus;
}

