public void dialogClosed(xFormRun _formRun)
{
    validFromDate effectiveDate;
    validToDate expirationDate;
    CustInterestVersionRecId oldCustInterestVersionRecId;
    CustInterestVersion localCustInterestVersion;
    ISysDateEffectiveCreateVersion formSysDateEffectiveCreateVersion;

    super(_formRun);

    formSysDateEffectiveCreateVersion = _formRun as ISysDateEffectiveCreateVersion;

    if (_formRun.closedOk() && formSysDateEffectiveCreateVersion)
    {
        effectiveDate = formSysDateEffectiveCreateVersion.getEffectiveDate();
        expirationDate = formSysDateEffectiveCreateVersion.getExpirationDate();

        oldCustInterestVersionRecId = custInterestVersion.RecId;

        ttsbegin;
        localCustInterestVersion.ValidFrom = effectiveDate;
        localCustInterestVersion.ValidTo = expirationDate;
        localCustInterestVersion.CustInterest = custInterest.RecId;
        localCustInterestVersion.GraceDays = custInterestVersion.GraceDays;
        localCustInterestVersion.insert();

        sysDateEffectiveDataManager.copy(oldCustInterestVersionRecId, localCustInterestVersion.RecId);
        ttscommit;

        custInterest_ds.research(true);
    }
}

public void dialogClosed(xFormRun _formRun)
