public boolean tabChange(int _fromTab)
{
    #define.TabInterestEarnings(1)
    #define.TabInterestPayments(2)

    boolean ret;
    ret = super(_fromTab);

    switch (_fromTab)
    {
        case #TabInterestEarnings:
            if (!earningsCustInterestVersionDetail.checkMasterCurrencyRecord())
            {
                ret = false;
            }
            break;
        case #TabInterestPayments:
            if (!paymentsCustInterestVersionDetail.checkMasterCurrencyRecord())
            {
                ret = false;
            }
            break;
    }
    return ret;
}

