void clicked()
{
    if (paymentsCustInterestFee.CurrencyCode)
    {
        paymentsCustInterestFee.CustInterestVersionDetail = paymentsCustInterestVersionDetail.RecId;
        paymentsCustInterestFee.write();
    }

    super();
}

void clicked()
{
    element.openForms(paymentsCustInterestFee, menuitemDisplayStr(LanguageTxt), MenuItemType::Display, formStr(LanguageTxt));
}
