[Form]
 public final class Intrastat extends FormRun
 {
    #resAppl

    Num                     num;
    NumberSeq               numberSeq;
    Counter                 seqNumCounter;
    Intrastat               intrastat_Prev;
    boolean                 isXML_CZ;

    #ISOCountryRegionCodes

}

public void init()
{
    super();
    // <GEELV>
    if (IntrastatParameters::isLegalEntityInEEU())
    {
        intrastat_StatisticalValue.enabled(false);
        statisticalValue_StatisticalValue.enabled(false);
    }
    isXML_CZ = false;
    // </GEELV>
    StatisticalValue_MiscChargePerKg.visible(FeatureStateProvider::isFeatureEnabled(IntrastatMiscChargesPerKgFeature::instance()));

    if (TaxIntegrationUtils::isMultipleTaxIdEnabledForIntrastat()
        && !IntrastatReportingByTaxIdToggle::isInExclusionListOfCoutries())
    {
        IntrastatTransfer.visible(false);
        IntrastatReport.visible(false);
        General_VATNum.label("@IntrastatByTaxId:CounterpartyTaxRegistrationNumber");
    }

    this.showItalianFields(MultipleTaxIdReportingHelper::isLegalEntityOrAddressInCountryRegion([#isoIT], MultipleTaxIdScope::Intrastat));
}

private void showItalianFields(boolean _show)
{
    Corrections.visible(_show);
    Amount_reportInvoiceValueCur.visible(_show);
}

public void init()
{
    super();

    intrastat_ds.cacheAddMethod(tableMethodStr(Intrastat, itemCodeName));
}

display IntrastatIsValidImageRes checkIfError(Intrastat _intrastat)
{
    if (!_intrastat.RecId)
    {
        return 0;
    }

    if (!_intrastat.Log)
    {
        return #ImageOK;
    }

    return #ImageError;
}

void create(boolean append = false)
{
    if (!num)
    {
        numberSeq = NumberSeq::newGetNum(IntrastatParameters::numRefIntrastatDispatchId(), true);
        num             = numberSeq.num();
        seqNumCounter   = 0;
    }

    if (intrastat)
    {
        intrastat_Prev = intrastat.data();
    }

    super(append);
}

int active()
{
    int ret;

    ret = super();

    if (intrastat.Log)
    {
        buttonErrorLog.enabled(true);
    }
    else
    {
        buttonErrorLog.enabled(false);
    }

    if (intrastat.Correct == NoYes::Yes)
    {
        general_Compress.value(false);
        general_Compress.enabled(false);
        corrections.enabled(true);
    }
    else
    {
        general_Compress.enabled(true);
        corrections.enabled(false);
    }

    sendReceiveAddress.enabled(intrastat.Direction == IntrastatDirection::Import);

    intrastat_DS.object(fieldNum(Intrastat, ServicesDelivery_IT))
        .allowEdit(intrastat.ItemType_IT == IntrastatItemType_IT::Services);

    return ret;
}

void changedQty()
{
    if (intrastat.Qty && intrastat.ItemId)
    {
        intrastat.AdditionalUnits = IntrastatUnitUtil::calculateAdditionalUnitQuantity(intrastat.ItemId,
                                                                                       intrastat.Qty, 
                                                                                       intrastat.IntrastatCommodity, 
                                                                                       InventTransOrigin::findByInventTransId(intrastat.InventTransId).ItemInventDimId);
    }
}

public void initValue()
{
    seqNumCounter++;

    intrastat.DispatchId        = num;
    intrastat.SeqNum            = seqNumCounter;
    intrastat.TransactionCode   = intrastat_Prev.TransactionCode;
    intrastat.TransDate         = intrastat_Prev.TransDate;
    intrastat.AccountNum        = intrastat_Prev.AccountNum;
    intrastat.DebCredType       = intrastat_Prev.DebCredType;
    intrastat.CountryRegionId   = intrastat_Prev.CountryRegionId;

    if (IntrastatSpecMovement_CZ::isEnabled())
    {
        intrastat.IntrastatSpecMove_CZ   = intrastat_Prev.IntrastatSpecMove_CZ;
    }

    super();
}

[SysObsolete('Method is not used anymore.', true, 31\01\2016)]
void modified()
{
    super();
}

public void jumpRef()
{
    if (intrastat.Category)
    {
        super();
    }
    else
    {
        EcoResCategoryHierarchyRole ecoResCategoryHierarchyRole;
        if (intrastat.Direction == IntrastatDirection::Import)
        {
            ecoResCategoryHierarchyRole = EcoResCategoryHierarchyRole::getHierarchiesByRole(EcoResCategoryNamedHierarchyRole::Procurement);
        }
        else
        {
            ecoResCategoryHierarchyRole = EcoResCategoryHierarchyRole::getHierarchiesByRole(EcoResCategoryNamedHierarchyRole::Sales);
        }
        EcoResCategoryHierarchy ecoResCategoryHierarchy = EcoResCategoryHierarchy::find(ecoResCategoryHierarchyRole.CategoryHierarchy);

        Args args = new Args(formStr(EcoResCategory));
        args.record(ecoResCategoryHierarchy);
        args.caller(element);

        MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(EcoResCategoryHierarchyDetails), MenuItemType::Display);
        menuFunction.run(args);
    }
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    if (intrastat.Direction == IntrastatDirection::Import)
    {
        return EcoResCategory::lookupCategoryHierarchyRole(
            _formReferenceControl,
            EcoResCategoryNamedHierarchyRole::Procurement);
    }
    else
    {
        return EcoResCategory::lookupCategoryHierarchyRole(
            _formReferenceControl,
            EcoResCategoryNamedHierarchyRole::Sales);
    }
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    if (intrastat.Direction == IntrastatDirection::Import)
    {
        return EcoResCategory::resolveCategoryHierarchyRole(
            _formReferenceControl,
            EcoResCategoryNamedHierarchyRole::Procurement);
    }
    else
    {
        return EcoResCategory::resolveCategoryHierarchyRole(
            _formReferenceControl,
            EcoResCategoryNamedHierarchyRole::Sales);
    }
}

public void modified()
{
    super();

    if (intrastat.Correct == NoYes::No)
    {
        intrastat.MonthsOfYear  = MonthsOfYear::None;
        intrastat.QuarterOfYear = QuarterOfYear::None;
        intrastat.Yr            = year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
        corrections.enabled(false);
        general_Compress.enabled(true);
    }
    else
    {
        intrastat.Compress  = NoYes::No;
        general_Compress.value(false);
        general_Compress.enabled(false);
        corrections.enabled(true);
    }
    intrastat_ds.refresh();
}

void modified()
{
    super();

    intrastat.modifiedCountryRegionId();
}

public void modified()
{
    intrastat.Category = 0;
    super();
    intrastat_ds.rereadReferenceDataSources();

    sendReceiveAddress.enabled(intrastat.Direction == IntrastatDirection::Import);
}

void modified()
{
    super();

