public void lookup(FormControl _formControl, str _filterStr)
{
    if (IntrastatStatProc::isRelationByCommodityEnabled())
    {
        IntrastatStatProc::lookupStatProcId_CZ(_formControl, intrastat.IntrastatCommodity);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return EUSalesList::taxID_lookup(_formReferenceControl);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (intrastat.AccountNum
        && TaxVATNumTable::taxVATNumTableEnabled() 
        && (intrastat.DebCredType == ModuleInventCustVend::Cust || intrastat.DebCredType == ModuleInventCustVend::Vend))
    {
        TaxVATNumTable::lookupVATNum(General_VATNum, intrastat.getCustVendTableFromIntrastat());
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

public void jumpRef()
{
    if (intrastat.AccountNum
        && TaxVATNumTable::taxVATNumTableEnabled() 
        && (intrastat.DebCredType == ModuleInventCustVend::Cust || intrastat.DebCredType == ModuleInventCustVend::Vend))
    {
        TaxVATNumTable::jumpRefVATNum(intrastat.getCustVendTableFromIntrastat(), element);
    }
    else
    {
        super();
    }
}

