void enablePostPageCtrls()
{
    editTransDate.allowEdit(sysWizard.parmAdjustType() == InventAdjustType_RU::Transactions);
    corrAccount.allowEdit(!sysWizard.parmCorrAccountProfitLoss());
    editCorrPosting.allowEdit(!sysWizard.parmCorrAccountProfitLoss());
}

public void fetchData()
{
    TmpAccountSum               tmpSum;
    DimensionsAccountCriteria   accountCriteria;

    Query                       query;
    QueryBuildDataSource        qbds;

    Name                        secondaryFocusName;
    RefRecId                    secondaryFocus;
    MainAccount                 mainAccount;
    InventTransCurrency_RU      inventTransCurrency;

    LedgerBalanceMainAccountAmounts ledgerBalanceMainAccount;

    if (fetchedParams                                               &&
        conPeek(fetchedParams, 1) == balanceDimension               &&
        conPeek(fetchedParams, 2) == mainFocus                      &&
        conPeek(fetchedParams, 3) == secondaryFocus                 &&
        conPeek(fetchedParams, 4) == sysWizard.parmLedgerAccount()  &&
        conPeek(fetchedParams, 5) == toDate)
    {
        return;
    }

    accountCriteria = sysWizard.parmLedgerAccount();
    tmpSum          = null;

    dimValue.visible(balanceDimension);
    tmpAccountSum_Balance01.visible(balanceDimension);
    tmpAccountSum_Balance02.visible(!balanceDimension);
    tmpAccountSum_Balance03.visible(!balanceDimension);

    inventTransCurrency = sysWizard.parmInventTransCurrency();

    if (balanceDimension)
    {
        secondaryFocusName = focusComboBox.getText(focusComboBox.selection());
        secondaryFocus = DimensionHierarchy::findByTypeAndName(DimensionHierarchyType::Focus, secondaryFocusName).RecId;

        if (secondaryFocus)
        {
            dimValue.label(secondaryFocusName);

            totalBalanceMST = 0;

            LedgerTrialBalanceTmp ledgerTrialBalanceTmp;
            LedgerTrialBalanceContract trialBalanceContract = new LedgerTrialBalanceContract();
            trialBalanceContract.parmFromDate(fromDate);
            trialBalanceContract.parmToDate(toDate);
            trialBalanceContract.parmIncludeOpening(false);
            trialBalanceContract.parmIncludeClosingAdjustments(false);
            trialBalanceContract.parmIncludeClosingTransactions(false);
            trialBalanceContract.parmPostingLayers(con2List([CurrentOperationsTax::Current]));
            trialBalanceContract.parmPrimaryDimensionFocus(secondaryFocusName);

            Map dimRangeMap = new Map(Types::Int64, Types::String);
            dimRangeMap.insert(DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount), accountCriteria);
            trialBalanceContract.parmDimensionRangeMap(dimRangeMap);

            if (FeatureStateProvider::IsFeatureEnabled(GeneralLedgerBalanceFeature::instance()))
            {
                GeneralLedgerBalanceProvider::getBalanceStatus(Ledger::current());

                LedgerTrialBalanceDPV2 trialBalanceDP = new LedgerTrialBalanceDPV2();
                trialBalanceDP.parmDataContract(trialBalanceContract);
                trialBalanceDP.setTrialBalanceTmpTable(ledgerTrialBalanceTmp);

                ttsbegin;

                trialBalanceDP.processReport();

                ttscommit;
            }
            else
            {
                DimensionFocusUpdateBalance::updateBalance(
                    DimensionHierarchy::findByTypeAndName(DimensionHierarchyType::Focus, secondaryFocusName),
                    false,
                    true,
                    Ledger::current());

                ttsbegin;

                LedgerTrialBalanceDP trialBalanceDP = new LedgerTrialBalanceDP();
                trialBalanceDP.parmDataContract(trialBalanceContract);
                trialBalanceDP.setTrialBalanceTmpTable(ledgerTrialBalanceTmp);

                trialBalanceDP.processReport();

                ttscommit;
            }

            tmpSum.clear();
            tmpSum.AccountNum = accountCriteria;

            while select PrimaryFocus, EndingBalance from ledgerTrialBalanceTmp
                order by PrimaryFocus
            {
                tmpSum.Txt = ledgerTrialBalanceTmp.PrimaryFocus;
                tmpSum.Balance01  = ledgerTrialBalanceTmp.EndingBalance;
                totalBalanceMST  += tmpSum.Balance01;
                tmpSum.insert();
                markedSumSet.add(tmpSum.RecId);
            }
        }
    }
    else
    {
        mainAccount = MainAccount::findByMainAccountId(sysWizard.parmLedgerAccount());

        if (mainAccount.transactionAccount() || mainAccount.Type == DimensionLedgerAccountType::Total)
        {
            if (DimensionFocusBalanceUpdateByLedgerFlight::instance().isEnabled())
            {
                MainAccountEventArgs args = MainAccountEventArgs::construct();
                args.parmBalanceLedgerRecId(Ledger::current());

                MainAccount::updateBalancesWithArgs(args);
            }
            else
            {
                MainAccount::updateBalances();
            }

            ledgerBalanceMainAccount = LedgerBalanceMainAccountAmounts::construct();
            ledgerBalanceMainAccount.parmAccountingDateRange(fromDate, toDate);
            ledgerBalanceMainAccount.parmIncludeRegularPeriod(true);
            ledgerBalanceMainAccount.parmIncludeOpeningPeriod(false);
            ledgerBalanceMainAccount.parmIncludeClosingPeriod(true);
            ledgerBalanceMainAccount.parmIncludeClosingPeriodBySystem(false);

            ledgerBalanceMainAccount.calculateBalance(mainAccount);
            totalBalanceMST = inventTransCurrency == InventTransCurrency_RU::PrimaryCur ?
                ledgerBalanceMainAccount.getAccountingCurrencyBalance() :
                ledgerBalanceMainAccount.getReportingCurrencyBalance();
        }
        else
        {
            totalBalanceMST  = 0;
        }

        if (totalBalanceMST >= 0)
        {
            tmpSum.Balance02 = totalBalanceMST;
        }
        else
        {
            tmpSum.Balance03 = -totalBalanceMST;
        }

        tmpSum.Balance01 = totalBalanceMST;

        tmpSum.insert();
        markedSumSet.add(tmpSum.RecId);
    }

    sysWizard.parmCostAmount(totalBalanceMST);

    tmpAccountSum.setTmpData(tmpSum);

    query   = new Query();
    qbds    = query.addDataSource(tableNum(TmpAccountSum));
    tmpAccountSum_ds.query(query);
    tmpAccountSum_ds.query().dataSourceTable(tableNum(TmpAccountSum)).addSortField(fieldNum(TmpAccountSum, Qty01), SortOrder::Ascending);
    tmpAccountSum_ds.query().dataSourceTable(tableNum(TmpAccountSum)).addSortField(fieldNum(TmpAccountSum, AccountNum), SortOrder::Ascending);
    tmpAccountSum_ds.executeQuery();

    fetchedParams = [balanceDimension, mainFocus, secondaryFocus, sysWizard.parmLedgerAccount(), toDate];
}

void fillFocus(boolean _fill = true)
{
    focusComboBox.clear();
    focusComboBox.enabled(false);

    if (_fill)
    {
        mainFocus = DimensionHierarchy::getMainAccountFocus().RecId;

        if (mainFocus)
        {
            DimensionHierarchy dimHierFocus;
            DimensionHierarchyLevelView dimHierLevel;

            while select dimHierFocus group by Name
                where dimHierFocus.StructureType == DimensionHierarchyType::Focus
                   && dimHierFocus.RecId != mainFocus
                exists join dimHierLevel
                    where dimHierLevel.HierarchyRecId == dimHierFocus.RecId
                        && dimHierLevel.DimensionAttribute == DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount)
            {
                focusComboBox.add(dimHierFocus.Name);
            }

            if (focusComboBox.items())
            {
                focusComboBox.enabled(true);
            }
        }
        else
        {
            error("@GLS116080",'',SysInfoAction_MenuFunction::newMenuItemFormRun(menuItemDisplayStr(DimensionFocusTable), MenuItemType::Display, formControlStr(DimensionFocus, GridOverview), "@SYS83243"));
        }
    }
}

void init()
{
    super();

    if (element.args().caller())
    {
        sysWizard = element.args().caller();
        inventDimParm.InventLocationIdFlag = NoYes::Yes;
        inventDimParm.initProductDimensionsAllGroups();
        element.updateDesign(InventDimFormDesignUpdate::Init);
        sysWizard.tmpFormLookUp(tmpFormLookup);
        sysWizard.tmpAccountSum(tmpAccountSum);
        sysWizard.parmInventDimParm(inventDimParm);
        sysWizard.parmShowLedgerVaucher(true);
        element.balanceDimension(false);
        markedSumSet = new Set(Types::Int64);

        tmpLedgerDimensionCorrAccount.insert();
    }
    else
    {
        InventAdjustmentWizard_RU::main(element.args());
        element.closeCancel();
    }
}

