void printJournal()
{
    TmpFormLookUp   tmp;
    Args            args = new Args();
    tmp.data(tmpFormLookup);
    args.record(tmpFormLookup);
    args.caller(element);

    new MenuFunction((sysWizard.parmAdjustType() == InventAdjustType_RU::OnHand ?
                     menuitemOutputStr(InventAdjOnHand) : menuitemOutputStr(InventAdjTransaction)),
                     MenuItemType::Output).run(args);

    tmpFormLookup_ds.executeQuery();
    tmpFormLookup_ds.positionToRecord(tmp);
}

void run()
{
    element.setTexts();
    element.setupValues();

    super();
}

void selectAll(boolean _select = true)
{
    TmpAccountSum   tmp;
    CostAmount      costAmount = 0;

    tmp.setTmpData(tmpAccountSum);

    if (_select)
    {
        while select tmp
        {
            markedSumSet.add(tmp.RecId);
            costAmount += tmp.Balance01;
        }
    }
    else
    {
        markedSumSet = new Set(Types::Int64);
    }

    sysWizard.parmCostAmount(costAmount);
    tmpAccountSum_ds.research();
}

