public edit NoYes markSum(boolean _set, TmpAccountSum _tmpAccountSum, NoYes _value)
{
    if (_set)
    {
        if (_value)
        {
            markedSumSet.add(_tmpAccountSum.RecId);
            sysWizard.parmCostAmount(sysWizard.parmCostAmount() + _tmpAccountSum.Balance01);
        }
        else
        {
            markedSumSet.remove(_tmpAccountSum.RecId);
            sysWizard.parmCostAmount(sysWizard.parmCostAmount() - _tmpAccountSum.Balance01);
        }
    }

    return markedSumSet.in(_tmpAccountSum.RecId);
}

display Percent percentOfTotal(TmpAccountSum _tmpAccountSum)
{
    return (totalBalanceMST ? (_tmpAccountSum.Balance01 / (totalBalanceMST / 100)) : 0);
}

