public void onSegmentChanged(DimensionControlSegment _segment)
{
    RecId mainAccountId;

    super(_segment);

    mainAccountId = corrAccount.getValue();

    if (mainAccountId)
    {
        sysWizard.parmCorrLedgerAccount(
            LedgerDimensionDefaultingEngine::createLedgerDimension(
                LedgerDefaultAccountHelper::getDefaultAccountFromMainAccountRecId(mainAccountId),
                new List(Types::Class)));
    }
    else
    {
        sysWizard.parmCorrLedgerAccount(0);
    }
}

public void pageActivated()
{
    sysWizard.parmCorrLedgerAccount(tmpLedgerDimensionCorrAccount.LedgerDimensionAccount);
    showLedgerVaucher.optionValue(sysWizard.parmShowLedgerVaucher());
    super();
}

