    [Form]
    public class InventBOMComparison_CN extends FormRun
    {
        // to save all selected BOM Ids, including ''
        Array                             bomVersions;

        // to save all selectd product Ids, including ''
        Array                             productItems;

        InventBOMComparisonType_CN        comparisonType;
        InventBOMComparisonCostType_CN    comparisonCostType;

        InventDimCtrl_Frm                 inventDimFormSetupBOM;
        InventBOMComparison_CN            inventBOMComparison_CN;

        // Prefix of Product control's name
        #define.ProductCtlNamePrefix('Product')

        // Prefix of BOM Version contorl's name
        #define.BOMVersionCtlNamePrefix('BOMVersion')

        // Prefix of BOM quantity field's name in grid
        #define.BOMQuantityFieldNamePrefix('Grid_BOMQty')

        // Prefix of the name of tab page corresponding to BOM
        #define.BOMTabpageNamePrefix('TabBOM')

        // Name of overview tab page
        #define.OverviewTabPage('TabOverview')

        // Basic number of progress
        #define.BasicNumOfProgress(3)

        #define.CurrentVersion(1)
        #localmacro.CurrentList
            comparisonType,
            comparisonCostType
        #endmacro
}

/// <summary>
///     Clears a Product control's corresponding to BOM Version control's content.
/// </summary>
/// <param name="_productCtl">
///     Product control.
/// </param>
public void clearMatchedBOMVersionCtl(FormStringControl _productCtl)
{
    FormStringControl   versionCtl;
    int                 indexNumber;

    if (_productCtl)
    {
        // get the matched bom version ctl
        indexNumber = element.getControlIndexNumber(_productCtl.name());
        if (indexNumber > 0)
        {
            versionCtl = element.design().controlName(strfmt('%1%2', #BOMVersionCtlNamePrefix, indexNumber));

            // clear old BOM version
            if (versionCtl)
            {
                versionCtl.text('');
            }
        }
    }
}

    public void close()
{
    super();
    comparisonType      = compareField.selection();
    comparisonCostType  = costType.selection();
    bomVersions         = this.getBOMVersions();
    productItems        = this.getProductIds();
    xSysLastValue::saveLast(this);
}

/// <summary>
///     If the selected BOM Ids are more than 1 and have no repeat,
///  use the selected parameters to do the comparison. Then active the
///  'Overview' tab page and show the result in it.
/// </summary>
public void compareBOMVersions()
{
    bomVersions = this.getBOMVersions();
    inventBOMComparison_CN = InventBOMComparison_CN::construct(bomVersions);
    // check BOM versions
    if (inventBOMComparison_CN.checkBOMVersions())
    {
        comparisonType      = compareField.selection();
        comparisonCostType  = costType.selection();
        // initialize comparison type
        inventBOMComparison_CN.initComparisonType(
            comparisonType,
            comparisonCostType);

        // do comparison and reset the grid's datasource for overview tab.
        inventBOMComparisonTmp_CN.setTmpData(
            inventBOMComparison_CN.doComparison());

        // refresh BOM comparsions
        this.refreshBOMComparisons();
    }
}

/// <summary>
///     Saves the BOM IDs in BOM Version controls and return it.
/// </summary>
/// <returns>
///     An array of selected BOM Ids (BOM Versions)
/// </returns>
public Array getBOMVersions()
{
    FormStringControl   versionCtl;
    int                 i;
    str                 ctlName;
    Array               bomIds;

    bomIds = new Array(Types::String);
    for (i = 1; i <= InventBOMComparisonTmp_CN::getMaxComparisonNum(); i++)
    {
        ctlName = strfmt('%1%2', #BOMVersionCtlNamePrefix, i);

        versionCtl = element.design().controlName(ctlName);
        if (versionCtl)
        {
            bomIds.value(i, versionCtl.text());
        }
    }

    return bomIds;
}

/// <summary>
///     Some controls' name have a integer postfix as
/// theirs index number. Find this interger and return it.
/// </summary>
/// <param name="_ctlName">
///     The name of the control
/// </param>
/// <returns>
///     The index number of control. If cannot find a right
///  index number, return -1. The index number's range is
///  [1, #MaxComparisonNum].
/// </returns>
/// <remarks>
///     #MaxComparisonNum must less than 10.
/// </remarks>
private int getControlIndexNumber(str _ctlName)
{
    str     postFix;
    int     indexNumber = -1;

    if (_ctlName != '')
    {
        // Fetch the last char as postFix (the index char)
        postFix = strLFix(strReverse(_ctlName), 1);

        if (isInteger(postFix))
        {
            indexNumber = str2int(postFix);
            if (indexNumber     <= 0
                || indexNumber  >  InventBOMComparisonTmp_CN::getMaxComparisonNum())
            {
                indexNumber = -1;
            }
        }
    }

    return indexNumber;
}

